public boolean createAsReleasedProduct(ItemId _itemId, EcoResProductNumber _productNumber,EcoResDescription _desc)
{

    EcoResTrackingDimensionGroupRecId       localTrackingDimensionGroupId;
    EcoResStorageDimensionGroupRecId        localStorageDimensionGroupId;
    EcoResProductIdentifier                 ecoResProductLocalIdentifier;
    EcoResProductDimensionGroupRecId        localProductDimensionGroupId;
    InventTable                             inventTableLocal;
    HMInventTable                           hmInventTable;
    EcoResProductTranslation    ecoResProductTranslation;

    boolean                                 result = false;

      select * from inventTableLocal where inventTableLocal.ItemId == _itemId;

        if (!inventTableLocal)
        {

            // Create a product
            ecoResProductLocal = new SysDictTable(tableNum(EcoResDistinctProduct)).makeRecord();
            ecoResProductLocal.DisplayProductNumber= _productNumber;
            ecoResProductLocal.SearchName = _desc;
            ecoResProductLocal.ProductType = EcoResProductType::Item;
            ecoResProductLocal.write();

            ecoResProductLocalIdentifier.ProductNumber = _productNumber;
            ecoResProductLocalIdentifier.Product = ecoResProductLocal.RecId;
            ecoResProductLocalIdentifier.write();

            localTrackingDimensionGroupId = HMParameters::defaultInventTrackingDimension();
            localStorageDimensionGroupId = HMParameters::defaultInventStorageDimension();

            if (EcoResProductInventoryDimensionGroups::validateNewDimensionGroupsForProduct(
                    ecoResProductLocal.RecId, localProductDimensionGroupId, localStorageDimensionGroupId, localTrackingDimensionGroupId))
            {
                EcoResProductInventoryDimensionGroups::updateDimensionGroupsForProduct(
                    ecoResProductLocal.RecId, localProductDimensionGroupId, localStorageDimensionGroupId, localTrackingDimensionGroupId);
            }


            ecoResProductTranslation.Product        =   ecoResProductLocal.RecId;
            ecoResProductTranslation.Name           =   _desc;
            ecoResProductTranslation.Description    =   _desc;
            ecoResProductTranslation.LanguageId     =    SystemParameters::getSystemLanguageId();

            if (ecoResProductTranslation.validateWrite(true))
            {
                ecoResProductTranslation.insert();
            }

            // Release product to company
            EcoResProductReleaseManager::releaseProductWithLegalEntityData(
                    ecoResProductLocal.RecId
                    , CompanyInfo::findDataArea(curext()).RecId
                    , _itemId
                    , _desc);

            this.initPdsVendorCheckFromInventModelGroup(ecoResProductLocal.RecId);
            itemId= _itemId;


            ttsBegin;
            select forUpdate inventTableLocal
            where inventTableLocal.ItemId == itemId;

            inventTableLocal.HMProductType = HMInventType::HCPCS;
            inventTableLocal.PmfProductType = PmfProductType::BOM;
            inventTableLocal.update();
            hmInventTable.initValue();
            hmInventTable.InventTable = inventTableLocal.ItemId;
            hmInventTable.insert();
            ttsCommit;

            result = true;
        }

    return result;

}

private void createBOMTable(Name _bomName, InventSiteId _siteId)
{
    NumberSeq           numberSeq = BOMTable::numberSeq(false,true);

    bomTable.initValue();

    bomTable.bomId          = numberSeq.num();
   // bomTable.ItemGroupId    = _itemGroupId;
    bomTable.Name           = _bomName;
    bomTable.SiteId         = _siteId;
    bomTable.HMMedisysGender = BOMVersion.HMMedisysGender;
    bomTable.AgeFrom = BOMVersion.AgeFrom;
    bomTable.AgeTo = BOMVersion.AgeTo;
    bomTable.ProtocolId = BOMVersion.ProtocolId;

    if (!bomTable.validateWrite())
    {
        throw error("@SYS23020");
    }
    bomTable.insert();
    numberSeq.used();

}

private void createBOMVersion(ItemId _itemId)
{
    bomVersion.initValue();

    bomVersion.initFromInventTable(InventTable::find(_itemId));
    bomVersion.initFromBOMTable(bomTable);

    bomVersion.insert();
}

public void deActivateBOM()
{
    HcmWorker worker;
    HcmEmployment employe;

    BOMVersionApprove approve = BOMRouteVersionApprove::newBOMVersion(bomVersion);

    select firstOnly RecId from worker
    join * from employe where employe.Worker == worker.RecId && employe.EmploymentType == HcmEmploymentType::Employee
    && employe.LegalEntity ==CompanyInfo::find().RecId;

    approve.parmApprover(worker.RecId);
    approve.parmApproveBOM(NoYes::No);
    approve.parmRemove(NoYes::Yes);
    approve.runOperation();
}

public Num getNewNumSeqForProductNumber()
{
    SysLastValue            nextNumSeq;
    Num                     number;

    NumberSequenceTable NumberSeqTable;
    NumberSeq NumberSeq;

    //numberSeqReference  = InventParameters::numRefItemId();

    NumberSeqTable      = NumberSequenceTable::find(EcoResProductParameters::numRefProductNumber().NumberSequenceId);

    if(NumberSeqTable)
    {
        // generate Appointment order number if number sequence code is created.
        NumberSeq = NumberSeq::newGetNumFromId(NumberSeqTable.RecId, true);

        if(NumberSeq)
        {
            number = NumberSeq.num();
        }
    }

    return number;
}

