public void initPdsVendorCheckFromInventModelGroup(RecId ecoResProductId)
{
    InventTable item;
    InventModelGroupId      InventModelGroupId;
    ItemGroupId     InventItemGroupItem;
    InventItemGroupItem     InventItemGroupItemLocal;
    inventModelGroupItem inventModelGroupItem;
    ttsbegin;
    // update the AVL list
    item = InventTable::findByProduct(ecoResProductId, true);
    if (item.RecId)
    {

            InventModelGroupId = HMParameters::defaultInventModelGroupId();
            if (InventModelGroupId != "")

            {
                inventModelGroupItem.ItemId                  = item.ItemId;
                inventModelGroupItem.ItemDataAreaId          = item.DataAreaId;
                inventModelGroupItem.ModelGroupDataAreaId    = curExt2dataareaid(tableNum(InventModelGroup));
                inventModelGroupItem.ModelGroupId            = InventModelGroupId;
                inventModelGroupItem.insert();

            }
            InventItemGroupItem = HMParameters::defaultItemModelGroup();
            if (InventItemGroupItem != '0')
            {
                InventItemGroupItemLocal.ItemId                  = item.ItemId;
                InventItemGroupItemLocal.ItemDataAreaId          = item.DataAreaId;
                InventItemGroupItemLocal.itemGroupDataAreaId     = curExt2dataareaid(tableNum(InventItemGroup));
                InventItemGroupItemLocal.ItemGroupId             = InventItemGroupItem;
                InventItemGroupItemLocal.insert();
            }



             item.initPdsVendorCheckFromInventModelGroup();
             item.update();
    }
    ttscommit;
}

