private void searchProtocolByClient(HMClientAccount _hmClientAccount)
{
    if(!qbdsSearchProtocol)
    {
        qbdsSearchProtocol = BOMVersion_q.dataSourceTable(tableNum(BOMVersion)).addDataSource(tableNum(HMClientProtocol));
        qbdsSearchProtocol.addLink(fieldNum(BOMVersion,ItemId),fieldNum(HMClientProtocol,ItemId));
    }
    qbdsSearchProtocol.enabled(true);
    qbdsSearchProtocol.addRange(fieldNum(HMClientProtocol,ClientAccount)).value(SysQuery::value(_hmClientAccount));
    BomVersion_ds.executeQuery();
}

