public void write(Name _bomNAme)
{
    EcoResProductNumber productNumber;
    HMProtocolId        HMProtocolId;
    try
    {
        ttsBegin;
        HMProtocolId = this.getNewNumSeqForProtocolID();
        if(!HMProtocolId)
        {
            warning("@MAZ12657");
            ttsAbort;
            return;
        }
        _sequence.used();
        this.createAsReleasedProduct(BOMVersion.ItemId, HMProtocolId,_bomNAme);
        this.createBOMTable(_bomNAme, HMParameters::defaultServiceSiteId());
        this.createBOMVersion(itemId);
        //this.approveBOM();
        //this.activateBom();
        ttsCommit;
    }
    catch(Exception::Error)
    {
        ttsAbort;
        throw error("@SYS23020");

    }

}

public void create(boolean _append = false)
{
    NumberSequenceTable         numSeqTableInvent;
    NumberSequenceReference     numberSeqReferencePatientNum;

    super(_append);
    ttsBegin;
    _sequence = NumberSeq::newGetNum( NumberSeqReference::findReference(extendedTypeNum(ItemId)));
    numSeqTableInvent = NumberSequenceTable::find(_sequence.parmNumberSequenceId());
    if(numSeqTableInvent.Manual)
    {
        BOMVersion_ItemId1.allowEdit(numSeqTableInvent.Manual == true);
    }
    else
    {
        BOMVersion.ItemId = _sequence.num();
        BOMVersion_ItemId1.allowEdit(numSeqTableInvent.numAllowEdit());
    }
    ttsCommit;


}

public int active()
{
    int ret;

    ret = super();
    BOMVersion_ds.allowEdit(!BOMVersion.Active);
    HMProtocolNotes_ds.allowEdit(!BOMVersion.Active);
    HMClientProtocol_ds.allowEdit(!BOMVersion.Active);
    HMProtocolAttributeAptitude_ds.allowEdit(!BOMVersion.Active);
    BOM_ds.allowEdit(!BOMVersion.Active);
    //BOMVersion_ds.allowCreate(!BOMVersion.Active);
    HMProtocolNotes_ds.allowCreate(!BOMVersion.Active);
    HMClientProtocol_ds.allowCreate(!BOMVersion.Active);
    HMProtocolAttributeAptitude_ds.allowCreate(!BOMVersion.Active);
    BOM_ds.allowCreate(!BOMVersion.Active);
    BOMVersion_ds.allowDelete(!BOMVersion.Active);
    HMProtocolNotes_ds.allowDelete(!BOMVersion.Active);
    HMClientProtocol_ds.allowDelete(!BOMVersion.Active);
    HMProtocolAttributeAptitude_ds.allowDelete(!BOMVersion.Active);
    BOM_ds.allowDelete(!BOMVersion.Active);
    HMPriceOverrideDetails.enabled(!BOMVersion.Active);
    BOMVersion_ItemId1.text(BOMVersion.ItemId);
    element.updateActivateButton();
    if(!BOMVersion.isNewRecord())
    {
        BOMVersion_ItemId1.allowEdit(false);
    }
    return ret;
}

public void delete()
{
    InventTable InventTable;
    RecId  EcoResProduct;
    EcoResProduct EcoResProductTable;
    ItemId item = BOMVersion.ItemId ;

    super();
    EcoResProduct = InventTable::find(item).Product;
    ttsBegin;
    /*
    InventTable.skipDeleteActions(true);
    delete_from InventTable
            where InventTable.ItemId == item;
    */
    while select forupdate *from  InventTable
            where InventTable.ItemId == item
    {
        InventTable.skipDeleteActions(true);
        InventTable.delete();
    }
    while select forupdate *from EcoResProductTable
        where EcoResProductTable.RecId == EcoResProduct
    {
        EcoResProductTable.skipDeleteActions(true);
        EcoResProductTable.delete();
    }
    /*
        EcoResProductTable.skipDeleteActions(true);
        delete_from  EcoResProductTable
        where EcoResProductTable.RecId == EcoResProduct;
    */

    ttsCommit;
}

public boolean validateWrite()
{
    boolean ret;

    ret = true;//super();
    if(BOMVersion.AgeFrom > BOMVersion.AgeTo)
    {
        warning(SysLabel::labelId2String2('@MAZ11061'));
        ret=false;
    }
    return ret;
}

public void write()
{
    if (BOMVersion.isNewRecord())
    {
        BOMVersion.ItemId = BOMVersion_ItemId1.text();
        element.write(BOMVersion.Name);
        BOMVersion_DS.research(true);
    }
    else
    {
        super();
        ttsBegin;
        select forupdate bomTable
        where bomTable.bomid == BOMVersion.BOMId;

        BOMTable.AgeFrom = BOMVersion.AgeFrom;
        BOMTable.AgeTo = BOMVersion.AgeTo;
        BOMTable.HMMedisysGender = BOMVersion.HMMedisysGender;
        BOMTable.update();
        ttsCommit;
    }
}

