public boolean validateWrite()
{
    boolean ret;
    BOM BOMLocal;
    ret = super();

    select count(RecId) from  BOMLocal
            where BOMLocal.BOMId == BOMVersion.BOMId
            && BOMLocal.ItemId == BOM.ItemId;

    if(Bom.RecId == 0)
    {
        if (BOMLocal.RecId != 0 )
        {
           error(strFmt('Cannot create a record in BOM table. Reference: 0, %1.The record already exists.',BOM.ItemId));
           ret = false;
        }
    }

    return ret;
}

