public void lookup(FormControl _formControl, str _filterStr)
{
    Args    args;
    FormRun formRun;
    SysTableLookup      sysTableLookup = sysTableLookup::newParameters(tablenum(InventTable),_formControl);
    Query               query = new Query();
    QueryBuildDataSource     queryBuildDataSource,qbd1,qbd2;
    QueryBuildRange     queryBuildRange;



    //queryBuildDataSource = query.addDataSource(tableNum(BOM));
    //queryBuildDataSource.addGroupByField(fieldNum(BOM,ItemId));
    qbd1 = query.addDataSource(tableNum(InventTable));
    qbd1.addRange(fieldNum(InventTable,HMProductType)).value(SysQuery::value(HMInventType::HCPCS));
    qbd1.addRange(fieldNum(InventTable,PmfProductType)).value(SysQuery::value(PmfProductType::None));
    //qbd1.relations(true);

    qbd2  = qbd1.addDataSource(tableNum(HMInventTable));
    qbd2.addRange(fieldNum(HMInventTable,ChargeType)).value(SysQuery::value(HMChargeType::Immediate));
    qbd2.relations(true);

    sysTableLookup.addLookupfield(fieldNum(InventTable, ItemId));
    sysTableLookup.addLookupMethod('showExamName');


    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();
    if(ret)
    {
        if(HMClientProtocol.CalculationMethod == HMProtocolCalculationMethod::BottomUp && HMClientProtocol.orig().CalculationMethod == HMProtocolCalculationMethod::PriceOverride)
        {
            if(Box::yesNo("@MAZ12113",DialogButton::No,"@MAZ7757") == DialogButton::No)
            {
                ret = false;
            }
        }
    }
    return ret;
}

public int active()
{
    int ret;

    ret = super();
    if(ret)
    {
        HMPriceOverrideDetails.enabled(HMClientProtocol.CalculationMethod == HMProtocolCalculationMethod::PriceOverride);
    }
    return ret;
}

public void write()
{
    Object formRun;

    Args args;

    if((HMClientProtocol.isNewRecord() && HMClientProtocol.CalculationMethod == HMProtocolCalculationMethod::PriceOverride) || (HMClientProtocol.CalculationMethod == HMProtocolCalculationMethod::PriceOverride && HMClientProtocol.orig().CalculationMethod != HMProtocolCalculationMethod::PriceOverride))
    {
        if(HMClientProtocol.CalculationMethod == HMProtocolCalculationMethod::PriceOverride)
        {
            args = new Args();
            args.name(formstr(HMPriceOverrideDetails));
            args.record(HMClientProtocol);

            formRun = ClassFactory.formRunClass(args);
            formRun.init();
            formRun.run();
            //formRun.wait();
            if(formRun.validateData())
            {
                super();
                formRun.saveData(HMClientProtocol.RecId);
            }
        }

    }
    else
    {

        super();
        if(HMClientProtocol.CalculationMethod == HMProtocolCalculationMethod::BottomUp)
        {
            HMClientProtocolPriceClass::deleteByClientProtocolRecId(HMClientProtocol.RecId);
        }

    }
}

