public void lookup()
{
    // super();
    Query                   query = new Query();
    QueryBuildDataSource    qbds;

    SysTableLookup          sysTableLookup  = SysTableLookup::newParameters(tableNum(AssetTable), this);

    // For filtering data in lookup
    qbds = query.addDataSource(tableNum(AssetTable));
    qbds.addRange(fieldnum(AssetTable, HMBuildingUnit)).value(SysQuery::value(0));
    sysTableLookup.addLookupfield(fieldNum(AssetTable, AssetId), true);
    // true/false â€“ optional value for displaying that field in lookup.
    sysTableLookup.addLookupfield(fieldNum(AssetTable, Name));

    sysTableLookup.parmQuery(query);
    sysTableLookup.performFormLookup();


}

public boolean modified()
{
    AssetTable  AssetTable;
    boolean ret;

    ret = super();

    if (ret)
    {
        if (this.valueStr() != "")
        {
            select RecId, AssetId from AssetTable
                where AssetTable.AssetId == this.valueStr();

            if (AssetTable.RecId)
            {
                AssetDescription.text(AssetTable::find(this.valueStr()).Name);
                ShortName.text(AssetTable::find(this.valueStr()).Name);
            }
            else
            {
                warning(strFmt("@SYS316757","@SYS67376"));
                this.text("");
                AssetDescription.text("");
                ShortName.text("");
            }
        }
    }

    return ret;
}

public void lookup()
{
    // super();

    Args args = new Args();
    FormRun itemLookUp;

    args.name(formstr(HMBuildingUnitCategoryLookupNew));
    args.caller(element);

    itemLookUp = ClassFactory.formRunClass(args);
    itemLookUp.init();
    this.performFormLookup(itemLookUp);

}

public boolean modified()
{
    HMBuildingUnitCategory  HMBuildingUnitCategory;
    HMBuildingUnitType      HMBuildingUnitType;
    boolean ret;

    ret = super();

    if (ret)
    {
        if (this.valueStr() != "")
        {
            select * from HMBuildingUnitCategory
                join * from HMBuildingUnitType
                where HMBuildingUnitCategory.HMBuildingUnitType == HMBuildingUnitType.RecId
                && (HMBuildingUnitType.Indicators == HMBuildingUnitTypeIndicator::BedLocation || HMBuildingUnitType.Indicators == HMBuildingUnitTypeIndicator::Building)
                && HMBuildingUnitCategory.Description == this.valueStr();

            if (HMBuildingUnitCategory.RecId)
            {
                element.setCategory(HMBuildingUnitCategory.RecId);
            }
            else
            {
                warning(strFmt("@SYS316757","@MAZ2483"));
                this.text("");
                element.setCategory(0);
            }
        }
        else
        {
            element.setCategory(0);
        }
    }

    return ret;
}

public void lookup()
{
    Args args = new Args();
    FormRun itemLookUp;

    args.name(formstr(HMBuildingUnitLookup));
    args.caller(element);
    args.parm(int642str(categoryId));
    itemLookUp = ClassFactory.formRunClass(args);
    itemLookUp.init();
    this.performFormLookup(itemLookUp);

}

public boolean modified()
{
    HMBuildingUnit  HMBuildingUnit;
    boolean ret;

    ret = super();

    if (ret)
    {
        if (this.valueStr() != "")
        {
            select Description, RecId from HMBuildingUnit
                where HMBuildingUnit.Description == this.valueStr()
                && HMBuildingUnit.HMBuildingUnitCategory == HMBuildingUnitCategory::find(categoryId).HMBuildingUnitCategory;

            if (HMBuildingUnit.RecId)
            {
                element.setParentID(HMBuildingUnit.RecId);
            }
            else
            {
                warning(strFmt("@SYS316757","@MAZ1970"));
                this.text("");
                element.setParentID(0);
            }
        }
        else
        {
            element.setParentID(0);
        }

    }

    return ret;
}

void clicked()
{
    HMBuildingUnit                  HMBuildingUnitLocal;
    AssetTable                      AssetTableLocal;
   FormRun                         formRun;
    Object                          formRunObject;
    super();


    HMBuildingUnitLocal.initValue();
    HMBuildingUnitLocal.Code                    = AssetCode.valueStr();
    HMBuildingUnitLocal.Description             = AssetDescription.valueStr();
    HMBuildingUnitLocal.ShortName               = ShortName.valueStr();
    HMBuildingUnitLocal.HMBuildingUnitCategory  = categoryId;
    HMBuildingUnitLocal.HMBuildingUnit          = parentUnitRecId;
    HMBuildingUnitLocal.ToDate                  = 31\12\2154;

    if (HMBuildingUnitLocal.validateWrite())
    {
        HMBuildingUnitLocal.insert();

        if (HMBuildingUnitLocal.RecId)
        {
            ttsBegin;
            select forUpdate AssetTableLocal
                where AssetTableLocal.AssetId == AssetCode.valueStr();

            AssetTableLocal.HMBuildingUnit =  HMBuildingUnitLocal.RecId;
            AssetTableLocal.update();
            ttsCommit;

        }

        formRun = element.args().caller();

        if (formHasMethod(formRun, identifierstr(refresh)))
        {
            formRunObject = formRun;
            formRunObject.refresh(HMBuildingUnitLocal.RecId);
        }
        element.close();
    }
}
