{
    boolean ret = true;

    if (Preemployment.checked() && PreemploymentProtocol.text() == "")
    {
        warning(SysLabel::labelId2String2('@MAZ11057'));
        ret = false;
    }
    if (Executive.checked() && ExecutiveProtocol.text() == "")
    {
        warning(SysLabel::labelId2String2('@MAZ11058'));
        ret = false;
    }
    return ret;
}

