    if (ret)
    {
        BirthDate.dateValue(hmAgeHelper.getDateFromAgeInDMY(HMPatient_AgeInDays.value(),HMPatient_AgeInMonths.value(),HMPatient_HMAgeValue.value()));
        element.isValidCreate();
    }
    return ret;
}

