public class HMOrganizationalCategory extends FormRun
{
    recId                   ParentID;
    ImageRes                imageRes;
    recid                   lastSelected;
    TreeItemIdx             lastSelectedIdx;
    boolean                 hasChildDept;
    boolean                 isNewClicked;
}

public boolean childDepartmentExist(int64 _Recid)
{
    HMOrganizationaCategory     hmOrganizationaCategoryLocal;
    select count(recId) from hmOrganizationaCategoryLocal where
            hmOrganizationaCategoryLocal.HMOrganizationaCategory == _Recid
            && hmOrganizationaCategoryLocal.Indicator == HMOrganizationalCategoryIndicator::DepartmentalUnit;

    if(hmOrganizationaCategoryLocal.recId==0)
    {
        return false;
    }
    else
    {
        return true;
    }
}

public void init()
{
    super();
    this.parentCall();
    hasChildDept = false;
    isNewClicked = false;
}

