public void setParentID(recId _parentID,boolean _hasChildDept)
{
    HMOrganizationaCategory     hmOrganizationCategory;
    hasChildDept = _hasChildDept;
    ParentID = _parentID;
    select * from hmOrganizationCategory where hmOrganizationCategory.RecId == ParentID;
    HMOrginzationalCategory_Description.text(hmOrganizationCategory.Description);
}

private void treeFill(TreeItemIdx _RecId,int64 _RecIdx)
{
    TreeItemIdx                         parentItemIdx;
    boolean                             hasChild;
    HMOrganizationaCategory             HMOrganizationaCategorylocalForChildCheck;
    HMOrganizationaCategory             hmOrganizationaCategorylocalForTree;
    hasChild = false;

    while select * from hmOrganizationaCategorylocalForTree
        where hmOrganizationaCategorylocalForTree.HMOrganizationaCategory == _RecIdx
    {
        while select * from HMOrganizationaCategorylocalForChildCheck
            where HMOrganizationaCategorylocalForChildCheck.HMOrganizationaCategory == hmOrganizationaCategorylocalForTree.RecId
        {
            hasChild = true;
        }
        if(hasChild)
        {
            parentItemIdx = SysFormTreeControl::addTreeItem(Tree,hmOrganizationaCategorylocalForTree.Description,
                                                                _RecId,hmOrganizationaCategorylocalForTree.RecId,imageRes,1);
            if(lastSelected == hmOrganizationaCategorylocalForTree.RecId)
            {
                lastSelectedIdx = parentItemIdx;
            }
            this.treeFill(parentItemIdx,hmOrganizationaCategorylocalForTree.RecId);
            hasChild = false;
        }
        else
        {
            parentItemIdx = SysFormTreeControl::addTreeItem(Tree,hmOrganizationaCategorylocalForTree.Description,
                                                                _RecId,hmOrganizationaCategorylocalForTree.RecId,imageRes,0);
            if(lastSelected == hmOrganizationaCategorylocalForTree.RecId)
            {
                lastSelectedIdx = parentItemIdx;
            }
        }


    }

}

public boolean validateWrite()
{
    boolean ret;

    ret = super();
    if(ret)
    {
        if (Indicator.selection() != HMOrganizationalCategoryIndicator::DepartmentalUnit)
        {
            if(hasChildDept)
            {
                Box::stop("@MAZ2912");
                ret = false;
            }
        }
    }
    return ret;
}

public void write()
{

    super();

    if(HMOrginzationalCategory_Description.valueStr() == "")
    {
        HMOrganizationaCategory.HMOrganizationaCategory = 0;
    }
    else
    {
        HMOrganizationaCategory.HMOrganizationaCategory = ParentID;
    }
    //ParentID=0;
    lastSelected = HMOrganizationaCategory.RecId;

    element.ParentCall();
    if (!isNewClicked)
    {
        tree.select(lastSelectedIdx);
        isNewClicked = false;
    }
}

/// <summary>
///
/// </summary>
/// <param name = "_append"></param>
public void create(boolean _append = false)
{
    super(_append);
    HMOrginzationalCategory_Description.enabled(true);
    HMOrginzationalCategory_Description.text("");
    isNewClicked = true;
}

public void selectionChanged(FormTreeItem _OldItem, FormTreeItem _NewItem, FormTreeSelect _how)
{
    HMOrganizationaCategory        hmOrganizationaCategorylocal;
    FormTreeItem selectedTreeItem = tree.getItem(tree.getSelection());
    super(_OldItem, _NewItem, _how);
    select * from hmOrganizationaCategorylocal
        where hmOrganizationaCategorylocal.RecId == selectedTreeItem.data();

    if(tree.getRoot() == tree.getSelection())
    {
        Indicator.text("");
        HMOrganizationaCategory_q.dataSourceTable(tableNum(HMOrganizationaCategory)).clearRanges();
        HMOrganizationaCategory_q.dataSourceTable(tableNum(HMOrganizationaCategory)).addRange(fieldNum(HMOrganizationaCategory,recId)).value('0');
        HMOrganizationaCategory_ds.executeQuery();
        HMOrginzationalCategory_Description.enabled(false);
        HMOrginzationalCategory_Description.text("");
        HMOrginzationalCategory_Description.update();


    }
    else
    {
        HMOrginzationalCategory_Description.enabled(true);
        HMOrganizationaCategory_q.dataSourceTable(tableNum(HMOrganizationaCategory)).clearRanges();
        HMOrganizationaCategory_q.dataSourceTable(tableNum(HMOrganizationaCategory)).addRange(fieldNum(HMOrganizationaCategory,recId)).value(int642str(hmOrganizationaCategorylocal.RecId));
        //HMOrganizationaCategory_ds.findRecord(hmOrganizationaCategorylocal);
        HMOrganizationaCategory_ds.executeQuery();
        ParentID = HMOrganizationaCategory.HMOrganizationaCategory;
        HMOrginzationalCategory_Description.text(HMOrganizationaCategory::find(ParentID).Description);
        hasChildDept = element.childDepartmentExist(ParentID);
    }

}

public void lookup()
{
    Args args = new Args();
    FormRun itemLookUp;


    args.name(formstr(HMOrganizationalCategoryLookup));
    args.caller(element);
    args.record(HMOrganizationaCategory);
    itemLookUp = ClassFactory.formRunClass(args);
    itemLookUp.init();
    this.performFormLookup(itemLookUp);
}

