public void checkIndicator(RecId _categoryId)
{
    if(HMOrganizationaCategory::find(_categoryId).Indicator == HMOrganizationalCategoryIndicator::OutpatientTreatment ||
       HMOrganizationaCategory::find(_categoryId).Indicator == HMOrganizationalCategoryIndicator::DepartmentalUnit ||
       HMOrganizationaCategory::find(_categoryId).Indicator == HMOrganizationalCategoryIndicator::OtherUnit ||
       _categoryId == 0)
    {
     HMOrganizationalUnit_HMOrganizationalUnitIndicators.enabled(false);
    }
    else
    {
     HMOrganizationalUnit_HMOrganizationalUnitIndicators.enabled(true);
    }
}

public void enableDisableLocation()
{
    HMOrganizationaCategory     hmOrganizationaCategory;
    HMOrganizationalUnit        hmOrganizationalUnitLocal;

    select RecId, HMOrganizationaCategory from hmOrganizationaCategory
        where hmOrganizationaCategory.RecId == CategoryID;

    select count(recId) from hmOrganizationalUnitLocal
        where hmOrganizationalUnitLocal.HMOrganizationaCategory == hmOrganizationaCategory.HMOrganizationaCategory;

    if (hmOrganizationalUnitLocal.RecId > 0)
    {
        HMOrgUnitDesc.enabled(true);
    }
    else
    {
        HMOrgUnitDesc.enabled(false);
        HMOrgUnitDesc.text("");
    }

}

