private void treeFill(TreeItemIdx _RecId,int64 _RecIdx)
{
    TreeItemIdx                     parentItemIdx;
    boolean                         hasChild;
    HMOrganizationalUnit            hmOrganizationalUnitlocalForChildCheck;
    HMOrganizationalUnit            hmOrganizationalUnitForTree;
    hasChild = false;

    while select HMOrganizationalUnit, RecId, Description from hmOrganizationalUnitForTree
        where hmOrganizationalUnitForTree.HMOrganizationalUnit == _RecIdx

    {
        while select HMOrganizationalUnit from hmOrganizationalUnitlocalForChildCheck
            where hmOrganizationalUnitlocalForChildCheck.HMOrganizationalUnit == hmOrganizationalUnitForTree.RecId
        {
            hasChild = true;
        }

        if(hasChild)
        {
            parentItemIdx = SysFormTreeControl::addTreeItem(Tree,hmOrganizationalUnitForTree.Description,
                                                                _RecId,hmOrganizationalUnitForTree.RecId,imageRes,1);
            if(lastSelected == hmOrganizationalUnitForTree.RecId)
            {
                lastSelectedIdx = parentItemIdx;
            }
            this.treeFill(parentItemIdx,hmOrganizationalUnitForTree.RecId);
            hasChild = false;
        }
        else
        {
            parentItemIdx = SysFormTreeControl::addTreeItem(Tree,hmOrganizationalUnitForTree.Description,
                                                                _RecId,hmOrganizationalUnitForTree.RecId,imageRes,0);
            if(lastSelected == hmOrganizationalUnitForTree.RecId)
            {
                lastSelectedIdx = parentItemIdx;
            }
        }

    }
}

