public void write()
{
    if(HMOrgUnitDesc.valueStr() == "")
    {
        HMOrganizationalUnit.HMOrganizationalUnit = 0;
    }
    else
    {
        HMOrganizationalUnit.HMOrganizationalUnit = ParentID;
    }

    if(HMBuildingUnit.valueStr() == "")
    {
        HMOrganizationalUnit.HMBuildingUnit = 0;
    }
    else
    {
        HMOrganizationalUnit.HMBuildingUnit = BuildingUnitID;
    }

    if (HMOrginzationalCategory_Description.valueStr() == "")
    {
        HMOrganizationalUnit.HMOrganizationaCategory = 0;
    }
    else
    {
        HMOrganizationalUnit.HMOrganizationaCategory = CategoryID;
    }

    if(HMOrganizationalUnit.orig().GenderSpecificOccupancy != HMOrganizationalUnit.GenderSpecificOccupancy)
    {
        if(!HMOrganizationalUnit.orig().GenderSpecificOccupancy)
        {
            HMBuildingUnit::updateGenderOccupancyByOU(HMOrganizationalUnit.RecId,HMOrganizationalUnit.GenderSpecificOccupancy, HMOrganizationalUnit.Gender,false);
        }
        else
        {
            if(Box::yesNo("@MAZ8221",DialogButton::Yes,"@SYS4784") == DialogButton::Yes)
            {
                HMBuildingUnit::updateGenderOccupancyByOU(HMOrganizationalUnit.RecId,HMOrganizationalUnit.GenderSpecificOccupancy, HMOrganizationalUnit.Gender,true);
            }
        }
    }
    else if(HMOrganizationalUnit.orig().GenderSpecificOccupancy == HMOrganizationalUnit.GenderSpecificOccupancy && HMOrganizationalUnit.GenderSpecificOccupancy)
    {
        if(HMOrganizationalUnit.orig().Gender != HMOrganizationalUnit.Gender)
        {
            if(Box::yesNo("@MAZ8221",DialogButton::Yes,"@SYS4784") == DialogButton::Yes)
            {
                HMBuildingUnit::updateGenderOccupancyByOU(HMOrganizationalUnit.RecId,HMOrganizationalUnit.GenderSpecificOccupancy, HMOrganizationalUnit.Gender,true);
            }
        }
    }

    if(HMOrganizationalUnit.orig().HMTreatmentClassification != HMOrganizationalUnit.HMTreatmentClassification)
    {
        if(!HMOrganizationalUnit.orig().HMTreatmentClassification)
        {
            HMBuildingUnit::updateTreatmentClassificationByOU(HMOrganizationalUnit.RecId,HMOrganizationalUnit.HMTreatmentClassification,false);
        }
        else
        {
            if(Box::yesNo("@MAZ8222",DialogButton::Yes,"@SYS4784") == DialogButton::Yes)
            {
                HMBuildingUnit::updateTreatmentClassificationByOU(HMOrganizationalUnit.RecId,HMOrganizationalUnit.HMTreatmentClassification,true);
            }
        }
    }

    super();

    lastSelected = HMOrganizationalUnit.RecId;


    element.ParentCall();
    tree.select(lastSelectedIdx);
}

public void modified()
{
    super();
    element.updateGroupGender();
}

