public void selectionChanged(FormTreeItem _OldItem, FormTreeItem _NewItem, FormTreeSelect _how)
{
    HMOrganizationalUnit        hmOrganizationalUnitlocal;
    HMOrganizationaCategory     hmOrganizationaCategory;
    FormTreeItem selectedTreeItem = tree.getItem(tree.getSelection());

    super(_OldItem, _NewItem, _how);
    if(selectedTreeItem)
    { 

        select RecId from hmOrganizationalUnitlocal
        where hmOrganizationalUnitlocal.RecId == selectedTreeItem.data();
    }

    element.resetIndicator();

    if(tree.getRoot() == tree.getSelection())
    {
        HMOrganizationalUnit_q.dataSourceTable(tableNum(HMOrganizationalUnit)).clearRanges();
        HMOrganizationalUnit_q.dataSourceTable(tableNum(HMOrganizationalUnit)).addRange(fieldnum(HMOrganizationalUnit,recId)).value('0' );
        HMOrganizationalUnit_ds.executeQuery();

        element.checkIndicator(HMOrganizationalUnit.HMOrganizationaCategory);
        HMOrgUnitDesc.enabled(false);
        HMOrgUnitDesc.text("");
        NewBuildingUnit.enabled(false);
        // added button for servcie category
        NewServiceCategory.enabled(false);
        Remove.enabled(false);
        HMOrginzationalCategory_Description.text("");
        HMOrginzationalCategory_Description.enabled(false);
        AddNewItem.enabled(false);
        AddFacilityId.enabled(false);
        AddChiefComplaint.enabled(false);
    }
    else
    {
        HMOrgUnitDesc.enabled(true);
        AddNewItem.enabled(true);
        AddFacilityId.enabled(true);
        AddChiefComplaint.enabled(true);
        NewServiceCategory.enabled(true);
        HMOrganizationalUnit_q.dataSourceTable(tableNum(HMOrganizationalUnit)).clearRanges();
        HMOrganizationalUnit_q.dataSourceTable(tableNum(HMOrganizationalUnit)).addRange(fieldnum(HMOrganizationalUnit,recId)).value(int642str(hmOrganizationalUnitlocal.RecId));
        HMOrganizationalUnit_ds.executeQuery();
        element.checkIndicator(HMOrganizationalUnit.HMOrganizationaCategory);
        ParentID = HMOrganizationalUnit.HMOrganizationalUnit;
        BuildingUnitID = HMOrganizationalUnit.HMBuildingUnit;
        CategoryID = HMOrganizationalUnit.HMOrganizationaCategory;
        HMOrgUnitDesc.text(HMOrganizationalUnit::find(ParentID).Description);
        HMBuildingUnit.text(HMBuildingUnit::find(BuildingUnitID).Description);
        HMOrginzationalCategory_Description.enabled(true);
        if(HMOrganizationalUnit.HMOrganizationaCategory != 0)
        {
            HMOrginzationalCategory_Description.text(HMOrganizationaCategory::find(HMOrganizationalUnit.HMOrganizationaCategory).Description);
        }
        if(HMOrganizationalUnit.IsBlocked == NoYes::Yes)
        {
            HMOrgUnit_HMBlockingReasonCode.enabled(true);
        }
        else
        {
            HMOrgUnit_HMBlockingReasonCode.enabled(false);
        }
        if(HMOrganizationalUnit.IsSpeciality==NoYes::No)
        {
            HMSpeciality.enabled(false);
        }

        else
        {
            HMSpeciality.enabled(true);
        }

        HMOrganizationalUnit_Code1.update();
        HMOrganizationalUnit_Description1.update();
        HMOrganizationalUnit_ShortName.update();

        HMOrgUnitDesc.update();
        HMBuildingUnit_FromDate.update();
        HMBuildingUnit_ToDate.update();
        HMOraganizationalUnit_IsBlocked.update();
        HMOrgUnit_HMBlockingReasonCode.update();
        HMBuildingUnit.update();
        HMOrganizationalUnit_IsPediatrics.update();
        HMOrganizationalUnit_IsIntensiveCareUnit.update();
        HMOrganizationalUnit_IsAdmittingFacility.update();
        HMOrganizationalUnit_IsSpeciality.update();
        HMSpeciality.update();
        HMOrginzationalCategory_Description.update();


    select RecId, Indicator from hmOrganizationaCategory
        where hmOrganizationaCategory.RecId == CategoryID;
        if(hmOrganizationaCategory.RecId != 0)
        {
            if(hmOrganizationaCategory.Indicator == HMOrganizationalCategoryIndicator::DepartmentalUnit)
            {
                NewBuildingUnit.enabled(false);
                Remove.enabled(false);
                GroupBlocking.enabled(false);
                GroupValidity.enabled(false);
