public void lookup()
{
    DimensionAttribute                  dimensionAttribute;

    DimensionAttributeDirCategory       dimAttributeDirCategory;

    Query                               query = new Query();

    SysTableLookup                      sysTableLookup;

    dimensionAttribute = DimensionAttribute::findByName('D40Location');

    super();

    // Only user defined dimensions will have a financial tag category

    if (dimensionAttribute.Type == DimensionAttributeType::CustomList)

    {

    select firstonly DirCategory from dimAttributeDirCategory where dimAttributeDirCategory.DimensionAttribute == dimensionAttribute.RecId;

    sysTableLookup = SysTableLookup::newParameters(tableNum(DimensionFinancialTag), this);

    // Add name field to be shown in the lookup form.

    sysTableLookup.addLookupfield(fieldNum(DimensionFinancialTag, Value));

    sysTableLookup.addLookupfield(fieldNum(DimensionFinancialTag, Description));

    query = new Query();

    query.addDataSource(tableNum(DimensionFinancialTag)).

    addRange(fieldNum(DimensionFinancialTag, FinancialTagCategory)).

    value(queryValue(dimAttributeDirCategory.DirCategory));

    sysTableLookup.parmQuery(query);

    // Perform the lookup.

    sysTableLookup.performFormLookup();

    }
}

public boolean modified()
{
    boolean                             ret;
    DimensionAttribute                  dimensionAttribute;
    DimensionAttributeDirCategory       dimAttributeDirCategory;
    DimensionFinancialTag               DimensionFinancialTag;
    ret = super();
    if(ret)
    {
        dimensionAttribute = DimensionAttribute::findByName('D40Location');
        select firstonly DirCategory from dimAttributeDirCategory where dimAttributeDirCategory.DimensionAttribute == dimensionAttribute.RecId;

        select Value,Description,REcId from DimensionFinancialTag
        where DimensionFinancialTag.FinancialTagCategory == dimAttributeDirCategory.DirCategory
        && DimensionFinancialTag.Value == this.text();

        if(DimensionFinancialTag.RecId != 0)
        {
            HMOrganizationalUnit.LocationDimension_Medisys = DimensionFinancialTag.Value;
            LocationDimensionDescription.text(DimensionFinancialTag.Description);
        }
        else
        {
            HMOrganizationalUnit.LocationDimension_Medisys = '';
            LocationDimensionDescription.text('');
            warning("@MAZ11529");
        }


    }
    return ret;
}

