public boolean modified()
{
    boolean                     ret;
    HMOrganizationaCategory      hmOrganizationaCategory;
    ret = super();
    if(ret)
    {
        select RecId, Indicator from hmOrganizationaCategory
            where hmOrganizationaCategory.RecId == CategoryID;

        if(hmOrganizationaCategory.Indicator == HMOrganizationalCategoryIndicator::DepartmentalUnit)
        {
            NewBuildingUnit.enabled(false);
            Remove.enabled(false);
            GroupBlocking.enabled(false);
            GroupValidity.enabled(false);
            Indicator.enabled(false);
            GroupPlanning.enabled(false);
            NewServiceCategory.enabled(false);
        }
        else
        {
            if(hmOrganizationaCategory.Indicator == HMOrganizationalCategoryIndicator::OtherUnit)
            {
                HMOrganizationalUnit_IsPediatrics.enabled(false);
                HMOrganizationalUnit_IsIntensiveCareUnit.enabled(false);
                HMOrganizationalUnit_IsSpeciality.enabled(false);
            }
            else
            {
                HMOrganizationalUnit_IsPediatrics.enabled(true);
                HMOrganizationalUnit_IsIntensiveCareUnit.enabled(true);
                HMOrganizationalUnit_IsSpeciality.enabled(true);
            }
            NewBuildingUnit.enabled(true);
            Remove.enabled(true);
            GroupBlocking.enabled(true);
            GroupValidity.enabled(true);
            Indicator.enabled(true);
            GroupPlanning.enabled(true);
            NewServiceCategory.enabled(true);
        }
        element.enableDisableLocation();
    }
    return ret;
}

