public void enter()
{
    if(CategoryID != 0)
    {
        element.resetIndicator();
        if(HMOrganizationaCategory::find(CategoryID).Indicator == HMOrganizationalCategoryIndicator::NursingAssignments)
        {
            this.delete(enum2str(HMOrganizationalUnitIndicators::PathologyAndClinicalLaboratory));
            this.delete(enum2str(HMOrganizationalUnitIndicators::RecoveryRoom));
            this.delete(enum2str(HMOrganizationalUnitIndicators::Surgery));
            this.delete(enum2str(HMOrganizationalUnitIndicators::DiagnosticImaging));
            this.delete(enum2str(HMOrganizationalUnitIndicators::Pharmacy));
            this.delete(enum2str(HMOrganizationalUnitIndicators::OutpatientCare));
            this.delete(enum2str(HMOrganizationalUnitIndicators::CriticalCareUnit));
            this.delete(enum2str(HMOrganizationalUnitIndicators::Rehabilitation));
        }
        if(HMOrganizationaCategory::find(CategoryID).Indicator == HMOrganizationalCategoryIndicator::TreatmentUnit)
        {
            this.delete(enum2str(HMOrganizationalUnitIndicators::Nursing));
            this.delete(enum2str(HMOrganizationalUnitIndicators::EmergencyCare));
            this.delete(enum2str(HMOrganizationalUnitIndicators::Isolation));
        }

        HMOrganizationalUnit_HMOrganizationalUnitIndicators.selectText(enum2str(HMOrganizationalUnit.HMOrganizationalUnitIndicators));
    }

    super();

}

