            a = (str2enum(hmOrganizationalUnitIndicatorsLocal,'Outpatient care'));
        }
        else if (indicatorValue == 'Nursing')
        {
            a = (str2enum(hmOrganizationalUnitIndicatorsLocal,'Nursing'));
        }
        else if(indicatorValue == 'Critical care unit')
        {
            a = (str2enum(hmOrganizationalUnitIndicatorsLocal,'Critical care unit'));
        }
        else if(indicatorValue == 'Surgery')
        {
            a = (str2enum(hmOrganizationalUnitIndicatorsLocal,'Surgery'));
        }
        else if(indicatorValue == 'RecoveryRoom')
        {
            a = (str2enum(hmOrganizationalUnitIndicatorsLocal,'RecoveryRoom'));
        }
        else if(indicatorValue == 'Pathology and clinical laboratory')
        {
            a = (str2enum(hmOrganizationalUnitIndicatorsLocal,'Pathology and clinical laboratory'));
        }
        else if(indicatorValue == 'Diagnostic imaging')
        {
            a = (str2enum(hmOrganizationalUnitIndicatorsLocal,'Diagnostic imaging'));
        }
        else if(indicatorValue == 'Pharmacy')
        {
            a = (str2enum(hmOrganizationalUnitIndicatorsLocal,'Pharmacy'));
        }
        else if(indicatorValue == 'Emergency care')
