/// <summary>
/// This function is called when a drill through is clicked on the chart
/// </summary>
/// <param name = "args">The context args, args.parmObject has a map of context including name and keys, args.record is empty but can set here</param>
/// <param name = "mf">The menu function that will get executed if true is returned</param>
/// <returns>True to run the menu function, false to cancel</returns>
public boolean drillThroughClicked(Args args, MenuFunction mf)
{
    Tutorial_ChartTable chartTable;
    Map map = args.parmObject();

    select firstonly chartTable where chartTable.RecId == str2int64(map.lookup("key"));
    args.record(chartTable);

    return true;
}

