public void close()
{
    Object formRun;
    super();
    formRun = element.args().caller();
    if(element.args().parm() == 'Appointment')
    {
        element.args().caller().getPatient(HMPatient.data(),true);
    }

    xArgs args = this.args();

    if(args.record().TableID == tableNum(HMPatient))
    {
        FormRun callerFormRun = args.caller() as FormRun;
        FormDataSource rootDataSource = getFirstRootDatasourceOnFormRun(callerFormRun);

        if(callerFormRun.form().name() != formStr(HMAllClaims))
        {
            rootDataSource.research();
        }
    }
}

public void createRecord(str _formDataSourceName, boolean _append = false)
{
    if (confind(mainDatasources, this.dataSource().id()))
    {
        partyForm.initValue();
        partyForm.create();
    }
    else
    {
        super(_formDataSourceName, _append);
    }
}

