        BtnGroupRegister.visible(false);
        BillingPane.visible(false);

        // <IN>
        if (SysCountryRegionCode::isLegalEntityInCountryRegion([#isoIN]))
        {
            HMPatientReferrals_DS.allowEdit(false);
        }
        // </IN>
    }
    else
    {
        if(_hmPatient.CustTable == 0)
        {
            HMPatientDeactivate.visible(false);
            BillingPane.visible(false);
            //HMPatientDeactivate.visible(true);
            HMPatientActive.visible(false);

            Maintain.enabled(true);
            bgNew.enabled(true);
            btngrpAccess.enabled(true);
            btngrpChart.enabled(true);
            bgAttachments.enabled(true);
            ShowGroup.enabled(true);
            TabAddressActionPane.enabled(true);
            TabCommunicationfContactInfoActionPane.enabled(true);
            PoliciesActionPane.enabled(true);
            DocumentsActionPane.enabled(true);
            ReferralsActionPane.enabled(true);
            //BillingPane.visible(true);
        }
        else
        {
            HMPatientDeactivate.visible(true);
            HMPatientActive.visible(false);

            Maintain.enabled(true);
            bgNew.enabled(true);
            btngrpAccess.enabled(true);
            btngrpChart.enabled(true);
