public void enableRfcCurpFields_MX(CustTable _custTable)
{
    boolean companyType;

    companyType = (_custTable.CompanyType_MX == CompanyType_MX::LegalEntity) || ( _custTable.CompanyType_MX == CompanyType_MX::LegalPerson);

    custTable_ds.object(fieldnum(CustTable, Rfc_MX)).enabled(companyType);
    custTable_ds.object(fieldnum(CustTable, Curp_MX)).enabled(companyType);
    custTable_ds.object(fieldnum(CustTable, StateInscription_MX)).enabled(companyType);
}

void getAllowedPartyTypes()
{
    ;
    if (dirPartyRelationship.RelationshipTypeId != dirRelationshipTypeTable.RelationshipTypeId)
    {
        dirRelationshipTypeTable.data(DirRelationshipTypeTable::find(dirPartyRelationship.RelationshipTypeId));
    }
    [allowedParentType,allowedChildType] = [dirRelationshipTypeTable.ParentPartyType,dirRelationshipTypeTable.ChildPartyType];
}

