public void initPriorities()
{
    int totalInsurances = HMPatientInsurance::getMinPriorityByPatient(HMPatient.RecId);

    if (totalInsurances > 1)
    {
        minimumPriority = totalInsurances;
        maximumPriority = 1;
    }
    else if (totalInsurances == 1)
    {
        minimumPriority = 1;
        maximumPriority = 1;
    }
    else
    {
        minimumPriority = 0;
        maximumPriority = 1;
    }

    element.setButtonsAccess();
}

public boolean isFilenameOk(Filename _filename)
{
    Filename    onlyname;
    str 1       dummy;
    str         ext;

    [dummy, onlyname, ext] = fileNameSplit(_filename);

    return onlyname != '' && ext != '';
}

public boolean isFileTypeOk(Filename _filename)
{
    DocuFileTypes   docuFileTypes;
    Filename        onlyname;
    str 1           dummy;
    str             ext;

    [dummy, onlyname, ext] = fileNameSplit(_filename);

    return !docuFileTypes.isEnabled(Global::strReplace(ext, '.', ''));
}

public void manageFields(boolean _switch = false)
{
    SysDictEnum enum = new SysDictEnum(enumNum(HideShowFields));
    if (!HideShowComboBox ||  !_switch)
        return;
    if (_switch)
    {
        HideShowComboBox.selection(!HideShowComboBox.selection());
        HideShowButton.text(enum.index2Label(!HideShowComboBox.selection()));
        HideShowButton.imageLocation(SysImageLocation::EmbeddedResource);
        HideShowButton.normalImage(HideShowComboBox.selection() == HideShowFields::Hide ? int2str(7886) : int2str(7882));
        HideShowButton.helpText(HideShowComboBox.selection() == HideShowFields::Hide ? "@SYS313915" : "@SYS313916");
    }
    //if(HideShowComboBox.selection() == HideShowFields::Hide)
    //{
        //Employement.visible(false);
        //EmploymentRightGroup.visible(false);
        //Billing.visible(false);
    //}
    //else
    //{
        //Employement.visible(true);
        //EmploymentRightGroup.visible(true);
        //if (isConfigurationkeyEnabled(configurationKeyNum(BillNClaimMgmt)))
        //{
            //Billing.visible(true);
        //}
//
    //}
}

