public void run()
{
    container                           vitalSignConLocal;
    HMPatient                           hmPatientLocal;
    HMResourceSchedulingPlanningObject  hmResourceSchedulingPlanningObjectLocal;
   //toDO: ax7 hmpatient  system windows control 
    // System.Windows.Controls.Image       img = new System.Windows.Controls.Image();
    Image                               axImg =  new Image();
    HMlookupClass                       HMlookupClassObj = new HMlookupClass();
    System.Xml.XmlDocument xmldoc;

    super();

    element.bindPatientNotes();

    xmldoc = new System.Xml.XmlDocument();


    if(element.args().dataset() == tableNum(CustTable))
    {
        select * from hmPatientLocal where hmPatientLocal.CustTable==element.args().record().RecId;
    }
    else if(element.args().dataset() == tableNum(HMPatient))
    {
        select * from hmPatientLocal where hmPatientLocal.RecId==element.args().record().RecId;
    }

    element.showHideFactBoxes();

   // TabPageDetails.tabAppearance(0);

    // initializing instance vairables to storing priority values
    element.initPriorities();

    // Code for Physician's calendar
    hmResourceSchedulingPlanningObjectLocal = objChartClass.getPatientPlanningObject(HMPatient.RecId);

    if(hmResourceSchedulingPlanningObjectLocal)
    {
        planObjRecId = hmResourceSchedulingPlanningObjectLocal.RecId;
        hmResourceSchedulingPlanningObject_ds.executeQuery();
    }
    else
    {
        planObjRecId = 0;
        hmResourceSchedulingPlanningObject_ds.executeQuery();
    }

    element.updatePatientType(hmPatientLocal);
    dirPartyPostalAddressView_ds.executeQuery();

}

public boolean selectControl(FormControl _control)
{
    boolean ret;
    if (dirPartyTable is DirPerson)
    {
        ret = partyForm.selectControl(_control, dirPersonName);

        if (!ret)
            return false;
    }

    ret = super(_control);

    return ret;
}

public void setButtonsAccess()
{
    // if lowest and highest priorities are equal.
    if (minimumPriority == maximumPriority)
    {
        bttnMoveUp.enabled(false);
        bttnMoveDown.enabled(false);
    }
    // if selected billing code priority is higest.
    else if (HMPatientInsurance.Priority == maximumPriority)
    {
        bttnMoveUp.enabled(false);
        bttnMoveDown.enabled(true);
    }
    // if selected billing code priority is lowest.
    else if (HMPatientInsurance.Priority == minimumPriority)
    {
        bttnMoveUp.enabled(true);
        bttnMoveDown.enabled(false);
    }
    else
    {
        bttnMoveUp.enabled(true);
        bttnMoveDown.enabled(true);
    }
    if (!HMPatientInsurance.RecId)
    {
        bttnMoveUp.enabled(false);
        bttnMoveDown.enabled(false);
    }
}

public  void showHideFactBoxes()
{
    FormRun                          formRunFactBox;
    int i;
    PartList                         PartListformRunFactBox  = new PartList(this);

    if(lineviewbutton.togglevalue()==1)
    {
        for ( i = 1; i <= partlistformrunfactbox.partcount(); i++)
        {
            formrunfactbox = partlistformrunfactbox.getpartbyid(i);

            if(element.args().parm() == 'Billing')
            {
                switch(formrunfactbox.name())
                {
                    case 'HMPatientInsuranceCarrierFactBox':
                    case 'CustStatisticsRecentActivity':
                    case 'CustStatisticsStatistics':
                    case 'smmRolesFactBox':
                    case 'smmRelationshipsFactBox':
                        formrunfactbox.design().visible(true);
                        break;
                }
            }
            else
            {
                switch(formrunfactbox.name())
                {
                    case 'HMPatientActiveAllergiesPart':
                    case 'HMPatientActiveProblemsPart':
                    case 'HMPatientCurrentMedicationPart':
                    case 'HMPatientContactsAddress':
                    case 'HMPatientHealthMaintenanceReminderPart':
                    case 'HMPatientContactPart':
                    case 'HMPatientInsuranceCarrierFactBox':
                        formrunfactbox.design().visible(true);
                        break;
                }
            }
        }
    }




}

