public void updateDesign(boolean _init = false)
{

    int PendingRefillCount;

    DirPartyType partyType = dirPartyTable.type();
    boolean      isInternalOrg = (partyType != DirPartyType::Organization && partyType != DirPartyType::Person);
//    boolean      isOrganization = (partyType == DirPartyType::Organization);
    boolean      isPerson = (partyType == DirPartyType::Person);
//
//    if (tabGeneral.isActivePage())
//    {
//        element.lock();
//
//        // Organization
//        orgGroup1.visible(!isPerson);
//        orgGroup2.visible(isOrganization);
//        orgPhoneticGroup.visible(isOrganization);
//        orgGroup3.visible(!isPerson);
//
//        // Person
//        personGroup1.visible(isPerson);
//        personGroup2.visible(isPerson);
//        personGroup3.visible(isPerson);
//
//        hideShowGroup.visible(!isInternalOrg);
//        element.unLock(true);
//    }
//
    if (!isInternalOrg)// && (_init || tabGeneral.isActivePage()))
    {
        if (_init)
        {
            partyForm.parmPartyType(partyType);
        }
        partyForm.setDatasourcePartySubType(isPerson ? dirPartyTable_DirPerson_ds : dirPartyTable_DirOrganizationBase_ds);
        partyForm.manageFields();
    }
}

public void updateDirPartyRelationshipRanges()
{
    QueryBuildDataSource qbd;
    QueryBuildRange qbRangeFrom;
    str qRange;

    qbd = dirPartyRelationship_ds.query().dataSourceTable(tableNum(DirPartyRelationship));
    qbRangeFrom = SysQuery::findOrCreateRange(qbd, fieldNum(DirPartyRelationship, ValidFrom));

    qRange = strFmt(DateEffectivenessCheck::queryRange(dateEffectiveActive.checked(),
                                                       dateEffectiveExpired.checked(),
                                                       dateEffectiveFuture.checked()),
                    qbd.name(),
                    fieldStr(DirPartyRelationship, ValidFrom),
                    fieldStr(DirPartyRelationship, ValidTo),
                    DateEffectivenessCheck::queryDate());

    qbRangeFrom.value(qRange);

    dirPartyRelationship_ds.executeQuery();
}

