//BP Deviation documented
display string30 patientAgeYMD()
{
    if (!((hasFieldAccess(tablenum(DirPerson),fieldnum(DirPerson, BirthMonth)))
        && (hasFieldAccess(tablenum(DirPerson),fieldnum(DirPerson, BirthDay)))
        && (hasFieldAccess(tablenum(DirPerson),fieldnum(DirPerson, BirthYear)))))
    {
        throw error("@SYS57330");
    }

    return hmPatient.getAgeNewWithParameters(dirPartyTable_DirPerson.birthDate());
}

