public int active()
{
    LogisticsAddressCountryRegion country;
    HcmIdentificationType HCMIdType;
    #Define.numericValues('1234567890')

    int ret;
    HMlookupClass                       HMlookupClassObj = new HMlookupClass();
    DirPartyRelationship    partyRelationship;

    ret = super();

    if(ret)
    {
        this.setPatientPicture();
        this.setSelfEmployedDesign();
        element.updateDesign();
        element.updateInsuranceTab();
        element.disableAllForInActive(HMPatient);
        if(HMPatient.Status == PatientStatus::Active)
        {
            if(CustTable)
            {
                HMPatientDeactivate.enabled(true);
            }
            else
            {
                HMPatientDeactivate.enabled(false);
            }
        }
        else
        {
            HMPatientDeactivate.enabled(false);
        }
        UpdateCallDate.enabled(HMPatient.ContactFirst);

                 //setDefaultPhysician
        if(HMPatient.HMClinicStaff == 0)
        {
            HMPatient.HMClinicStaff = HMlookupClassObj.getDefaultPhysician().RecId;
            Physician.text(HMClinicStaff::getPhysicianNameByRecId(HMPatient.HMClinicStaff));
        }
        else
        {
            Physician.text(objChartClass.getPrimaryPhysicianName(HMPatient.RecId));
        }

        select * from partyRelationship where partyRelationship.RecId == HMPatient.DirPartyRelationshipNextToKin;

        NextToKin.text(partyRelationship.childName());
        FamilyDoctor.text(HMClinicStaff::getPhysicianNameByRecId(HMPatient.HMClinicStaffFamilyDoctor));

        country = LogisticsAddressCountryRegion::find('sau');

        //if (HMPatient.LogisticsAddressCountryRegion == 0)
        if (!HMCountryRegionName.text())
        {
            HMPatient.HMIdentificationNumber = '';
            HMPatient_HMNationalIdType.enabled(false);
        }
        //else if (HMPatient.LogisticsAddressCountryRegion == country.RecId)
        //else if (HMCountryRegionName.text() == 'sau')
        //{
        //    HMPatient.HMNationalIdType = HMNationalIdType::Bataqa;
        //    //HMPatient_HMNationalIdType.enabled(false);
        //}
        //else
        //{
        //    if (HMPatient.HMNationalIdType == HMNationalIdType::Bataqa)
        //    {
        //        HMPatient.HMNationalIdType = HMNationalIdType::Iqama;
        //    }
        //    HMPatient_HMNationalIdType.enabled(true);

        //   // select firstOnly HCMIdType
        //     //   where HCMIdType.HMNationalIdType == HMIdentificationType::NationalId
        //     //       && HCMIdType.HMIdentificationPersonType == HMIdentificationPersonType::Patient;

        //    //HMPatientIdentificationType.enabled(false);

        //    //HMPatient.HCMNationalIdType(true, HCMIdType.IdentificationTypeId);

        //   // HMPatientIdentificationType.enabled(true);
        //}

        if (HMPatient.LogisticsAddressCountryRegion > 0 && HMPatient.HMNationalIdType != HMNationalIdType::Passport)
        {
            if (strLen(HMPatient.HMIdentificationNumber) != 10 && strLen(HMPatient.HMIdentificationNumber) !=0 )
            {
                ret = false;
                error(strFmt("The %1 number should be 10 digits long.", HMPatient.HMNationalIdType));
                HMPatient.HMIdentificationNumber = '';
            }
           // info(strFmt("%1--%2", strLen(_HCMIdentificationNumber), strLen(strKeep(_HCMIdentificationNumber, '1234567890'))));
            if (!(strLen(HMPatient.HMIdentificationNumber) == strLen(strKeep(HMPatient.HMIdentificationNumber, #numericValues))))
            {
                ret = false;
                error(strFmt("The %1 number can only be comprised of numeric digits.", HMPatient.HMNationalIdType));
                HMPatient.HMIdentificationNumber = '';
            }
        }

        //HMHijriDOB.text(HMHijriDateHelper::convertGeorgianToHijri(HMPatient.patientBirthDate()));
        //HMHijriExpiryDate.text(HMHijriDateHelper::convertGeorgianToHijri(HCMPersonIdentificationNumber_ExpirationDate.dateValue()));

    }

    if(HMPatient.CustTable !=0)
    {
        LogisticsLocationDefault.enabled(true);
    }


    return ret;
}

