public void initValue()
{
    super();

    // If customer form is called from other form
    if (callerRecord)
    {
        switch (callerRecord.TableId)
        {
            // If called by Lead or Opportunity, initialize customer values
            case tablenum(smmLeadTable) :
            case tablenum(smmOpportunityTable) :
                smmCreateEntity::initFromCommon(callerRecord, custTable);

                if (custTable.Party)
                {
                    dirPartyTable.data(DirPartyTable::findRec(custTable.Party));
                }
                break;

            case tablenum(DirPartyTable) :
                dirPartyTable.data(callerRecord);
                break;
        }

        if (dirPartyTable.RecId)
        {
            if (dirPartyTable.type() == DirPartyType::Person)
            {
                dirPersonName.data(DirPersonName::find(dirPartyTable.RecId));
            }
            custTable.Party = dirPartyTable.RecId;
            custTable.modifiedField(fieldnum(CustTable,Party));
        }
    }
}

