void write()
{
    boolean updateCurrency;
    NoYes   newRecord;

    updateCurrency = false;
    if (custTable.orig().Currency && custTable.Currency != custTable.orig().Currency)
    {
        if (custTable.existOpenOrders())
        {
            if (CustTable::promptConvertCurrencyCode(custTable.AccountNum))
            {
                updateCurrency = true;
            }
        }
    }

    ttsbegin;

    if (updateCurrency)
    {
        custTable.convertCurrencyCode();
    }

    newRecord = custTable.RecId ? NoYes::No : NoYes::Yes;

    custTable.Party = dirPartyTable.RecId;
    //Arif dimensionDefaultingController.writing();

    super();

    //Yahya partyForm.writeAddressBookParty();

    //Arif element.numberSeqFormHandler().formMethodDataSourceWrite();
