void delete()
{
    FormDataSource callerDataSource;

    ttsbegin;
    //Arif element.numberSeqFormHandler().formMethodDataSourceDelete();

    super();
    //Arif dimensionDefaultingController.deleted();

    if (element.args() && element.args().dataset() == tablenum(smmBusRelTable))
    {
        callerDataSource = FormDataUtil::getFormDataSource(element.args().record());
        if (callerDataSource)
        {
            callerDataSource.executeQuery();
        }
    }

    RecId custTableRecId = CustTable.RecId;
    if(objChartClass.markPatientInactive(custTableRecId))
    {
        CustTable_ds.executeQuery();
        CustTable_ds.positionToRecord(CustTable::findRecId(custTableRecId));
        Box::info('Patient successfully deleted');
        element.close();
    }
    ttscommit;
}

