/// <summary>
/// Checks if there exist any Customer/vendors with the same Enterprise Number as entered by user and show error
/// message if any such records are found.
/// Additionally, check is made that for VAT obliged Customer, VatNum = Enterprise Number(without leading text)
/// and, for NON VAT obliged Customer, VatNum = "".
/// </summary>
/// <returns>
/// true, if all checks are passed or the user accepts error conditions, else false.
/// </returns>
public boolean validate()
{
    #define.numberRangeSep('0123456789.')
    #define.NewLine("\n")
    boolean             ret;
    str                 message;
    TaxEnterpriseNumber taxEnterpriseNumberWithoutLeadingText;
    boolean             isVatObliged;
    ret = super();

    taxEnterpriseNumberWithoutLeadingText = strkeep(custTable.EnterpriseNumber, #numberRangeSep);
    isVatObliged                          = TaxEnterpriseBranchNumber_BE::checkVatObligation(custTable.EnterpriseNumber);

    // Check for Duplicates
    if (ret && custTable.EnterpriseNumber)
    {
        ret = TaxEnterpriseBranchNumber_BE::validateDuplicates(custTable.EnterpriseNumber, custTable.vatNum, custTable.AccountNum, ModuleCustVend::Cust);
    }

    // If Enterprise Number has leading text(VAT obliged) check if EnterpriseNum(number + dots) is same as VATNum
    if (ret && isVatObliged && custTable.vatNum && custTable.vatNum != taxEnterpriseNumberWithoutLeadingText)
    {
        message = strfmt("@SYS183525", ModuleCustVend::Cust, custTable.vatNum, taxEnterpriseNumberWithoutLeadingText);
        message = strfmt("@SYS183526", message, #NewLine);

        if (Box::yesNo(message, DialogButton::Yes) == DialogButton::No)
        {
            ret = false;
        }
    }
    else if (ret && custTable.vatNum && custTable.EnterpriseNumber && !isVatObliged) // VatNum should be empty for Non VAT obliged case
    {
        message = strfmt("@SYS183527", ModuleCustVend::Cust, custTable.vatNum,"@SYS183528");
        if (Box::yesNo(message, DialogButton::Yes) == DialogButton::No)
        {
            ret = false;
        }
    }

    return ret;
}

