public boolean validateWrite()
{
    boolean ret;

    HMInsuranceContractCarrierCombination   combination;
    HMInsuranceContract                     contract;
    HMInsuranceContractClassOrPlans         plans;
    RefRecId                                carrierRecId;

    ret = super();

    if (ret)
    {
        carrierRecId = HMInsuranceCarrier::findByCode(HMPatientInsurance_InsuranceCarrier.valueStr()).RecId;

        select firstOnly combination
            where combination.HMInsuranceCarrier == carrierRecId;


        if (combination)
        {
            select firstOnly HMInsuranceContract
                join combination
                where HMInsuranceContract.RecId == HMPatientInsurance.HMInsuranceContract
                    && HMInsuranceContract.HMInsuranceContractCarrierCombination == combination.recid
                    && combination.HMInsuranceCarrier   == carrierRecId;

            if (HMInsuranceContract)
            {
                plans.clear();

                if (HMInsuranceContract.HMContractStatus == HMContractStatus::Active)
                {
                    select plans
                        where plans.RecId == HMPatientInsurance.HMInsuranceContractClassOrPlans
                            && plans.HMInsuranceContract == HMInsuranceContract.RecId;

                    if (plans)
                    {
                        if (plans.HMContractClassStatus == HMContractClassStatus::Active)
                        {
                            ret = true;
                        }
                        else
                        {
                            ret = checkFailed("@MAZ13184");
                        }
                    }
                    else
                    {
                        ret = checkFailed("@MAZ13158");
                    }
                }
                else
                {
                    ret = checkFailed("@MAZ13185");
                }
            }
            else
            {
                ret = checkFailed("@MAZ13160");
            }
        }
        else
        {
            ret = checkFailed("@MAZ13159");
        }
    }

    return ret;
}

