public void delete()
{
    HMInsurancePolicyPriority  priority;

    priority = HMPatientInsurance.Priority;
    super();
    if (priority)
    {
        minimumPriority -= 1;
        this.research();
        if (maximumPriority != priority)
        {
            this.positionToRecordByValue(fieldNum(HMPatientInsurance, Priority), int2str(priority));
        }
        element.initPriorities();
    }
}

public void write()
{
    boolean isRecordInserted = false;
    if(hmPatientInsurance.RecId)
    {
        isRecordInserted = true;
    }

    super();


    if(!isRecordInserted)
    {
        minimumPriority = hmPatientInsurance.Priority;
    }
}

