public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    SysReferenceTableLookup     sysRefTableLookup;
    Query                       lookupQuery = new Query();
    QueryBuildDataSource        QbdsContract, QbdsCombination;

    // Construct the SysRefTableLookup object
    sysRefTableLookup = SysReferenceTableLookup::newParameters(tableNum(HMInsuranceContract), _formReferenceControl);

    // Add the field list that will be displayed on the lookup form
    sysRefTableLookup.addLookupfield(fieldNum(HMInsuranceContract, HMContractNumber));
    sysRefTableLookup.addLookupfield(fieldNum(HMInsuranceContract, HMContractName));

    // Construct the query's data source
    QbdsContract = lookupQuery.addDataSource(tableNum(HMInsuranceContract));
    QbdsCombination = QbdsContract.addDataSource(tableNum(HMInsuranceContractCarrierCombination));
    QbdsCombination.relations(true);

    // Add ranges to the query data source
    QbdsCombination.addRange(fieldNum(HMInsuranceContractCarrierCombination, HMInsuranceCarrier)).value(queryValue(HMInsuranceCarrier::findByCode(HMPatientInsurance_InsuranceCarrier.valueStr()).RecId));
   // lookupQueryDataSource.addRange(fieldNum(MyTable, MySecondField)).value(queryNotValue(''));

    // Pass the query to the lookup object
    sysRefTableLookup.parmQuery(lookupQuery);

    return sysRefTableLookup.performFormLookup();
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
