public int active()
{
    Object                              formRunObject;
    FormRun                             formRun;
    PartList                            pl = new PartList(element);
    int                                 i;
    int                                 ret;

    ret = super();

    if(ret)
    {
        element.updateDesign();
        for (i = 1; i <= pl.partCount(); i++)
        {
            formRun = pl.getPartById(i);
            if(formRun)
            {
                if (formHasMethod(formRun,identifierstr(refreshData)))
                {
                     formRunObject = formRun;
                     formRunObject.refreshData();
                }
            }
        }
    }

    return ret;
}

