public boolean validateWrite()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (dirPartyRelationship.ParentParty && dirPartyRelationship.ChildParty &&
            dirPartyRelationship.ParentParty != dirPartyTable.RecId &&
            dirPartyRelationship.ChildParty != dirPartyTable.RecId)
        {
            // Relationship must have '%1-%2' as one of the parties.
            ret = checkFailed(strFmt("@SYS116469",dirPartyTable.PartyNumber,dirPartyTable.Name));
        }
    }

    return ret;
}

public int active()
{
    int ret;

    ret = super();

    if (ret)
    {
        element.getAllowedPartyTypes();
        dirPartyRelationshipChild_ds.allowEdit(!dirPartyRelationship.RecId ? true : false);
        dirPartyRelationshipParent_ds.allowEdit(!dirPartyRelationship.RecId ? true : false);
    }

    return ret;
}

void updateRange()
{
    QueryBuildDataSource    qbd;
    QueryBuildRange         qbRange;
    str                     qStr;

    qbd = this.query().dataSourceTable(tableNum(DirPartyRelationship));
    qStr = strFmt('((%1.%2 == %4) || (%1.%3 == %4))',
           qbd.name(), fieldStr(DirPartyRelationship, ParentParty),
           fieldStr(DirPartyRelationship,ChildParty), dirPartyTable.RecId);

    qbRange = SysQuery::findOrCreateRange(qbd, fieldNum(DirPartyRelationship, RecId));
    qbRange.status(RangeStatus::Locked);
    qbRange.value(qStr);

}

public void executeQuery()
{
    super();
}

public void init()
{
    QueryBuildDataSource    qbd;
    QueryBuildRange qbr;

    super();
    qbd = this.query().dataSourceTable(tableNum(DirPartyRelationship));
    qbd.clearDynalinks();
    qbd.clearLinks();

    // These links are used so that we can indirectly constrain DirPartyRelationship using DirPartyRelationshipsUnionView which is a
    // constrained table in the GAB XDS policies
    qbd = this.query().dataSourceTable(tableNum(DirPartyRelationship)).addDataSource(tableNum(DirPartyRelationshipsUnionView));
    qbd.addLink(fieldNum(DirPartyRelationship, ChildParty), fieldNum(DirPartyRelationshipsUnionView, OtherParty));
    // Do not expose relation types that are used for XDS.
    qbr = SysQuery::findOrCreateRange(qbd, fieldNum(DirPartyRelationshipsUnionView, SystemType));
    qbr.value(strFmt('((%1.%2 < %3) || (%1.%2 > %4))',
        qbd.name(),
        fieldStr(DirPartyRelationshipsUnionView, SystemType),
        enum2int(DirSystemRelationshipType::Customer),
        enum2int(DirSystemRelationshipType::ProspectiveContInLE)));
    qbr.status(RangeStatus::Hidden);
    qbd.joinMode(JoinMode::ExistsJoin);

    qbd = this.query().dataSourceTable(tableNum(DirPartyRelationship)).addDataSource(tableNum(DirPartyRelationshipsUnionView));
    qbd.addLink(fieldNum(DirPartyRelationship, ParentParty), fieldNum(DirPartyRelationshipsUnionView, OtherParty));
    // Do not expose relation types that are used for XDS.
    qbr = SysQuery::findOrCreateRange(qbd, fieldNum(DirPartyRelationshipsUnionView, SystemType));
    qbr.value(strFmt('((%1.%2 < %3) || (%1.%2 > %4))',
        qbd.name(),
        fieldStr(DirPartyRelationshipsUnionView, SystemType),
        enum2int(DirSystemRelationshipType::Customer),
        enum2int(DirSystemRelationshipType::ProspectiveContInLE)));
    qbr.status(RangeStatus::Hidden);
    qbd.joinMode(JoinMode::ExistsJoin);
}

public boolean validate()
{
    boolean ret;
    ;
    ret = super();

    if (ret)
    {
        element.getAllowedPartyTypes();
        if ((allowedParentType != DirPartyType::None || allowedChildType != DirPartyType::None) &&
            (allowedParentType != dirPartyTable.type() && allowedChildType != dirPartyTable.type()))
        {
            // Relationship %1 is not allowed for type - %2
            return checkFailed(strFmt("@SYS116465",dirPartyRelationship.RelationshipTypeId, dirPartyTable.type()));
        }
        if (DirRelationshipTypeTable::relationshipTypeToSystemType(dirPartyRelationship.RelationshipTypeId) == DirSystemRelationshipType::ContactPerson)
        {
            // A %1 relationship can only be created by creating a contact person.
            return checkFailed(strFmt("@SYS116466",enum2str(DirSystemRelationshipType::ContactPerson)));
        }
    }

    return ret;
}

public void modified()
{
    super();
    //dirRelationshipTypeTable.data(DirRelationshipTypeTable::find(dirPartyRelationship.RelationshipTypeId));
//    dirPartyRelationshipParent.data(dirPartyTable);
    //dirPartyRelationship.ChildParty = dirPartyRelationshipChild.RecId;
    //dirPartyRelationship.ParentParty = dirPartyRelationshipParent.RecId;
}

public void init()
{
    QueryBuildDataSource    qbd;
    QueryBuildRange         qbr;

    super();

    // Add filter to exclude the Worker, Applicant and PersonalContact system relationship to LE
    qbd = this.query().dataSourceTable(tableNum(DirRelationshipTypeTable));
    if (qbd)
    {
        qbd.clearRange(fieldNum(DirRelationshipTypeTable, SystemType));
        qbr = qbd.addRange(fieldNum(DirRelationshipTypeTable, SystemType));
        if (qbr)
        {
            qbr.status(RangeStatus::Locked);
            qbr.value(strFmt('(%1 != %2) && (%1 != %3) && (%1 != %4) && (%1 != %5) && (%1 != %6)',
                    fieldStr(DirRelationshipTypeTable, SystemType),
                    queryValue(enum2int(DirSystemRelationshipType::Applicant)),
                    queryValue(enum2int(DirSystemRelationshipType::Worker)),
                    queryValue(enum2int(DirSystemRelationshipType::DisallowedVendor)),
                    queryValue(enum2int(DirSystemRelationshipType::PartyInLE)),
                    queryValue(enum2int(DirSystemRelationshipType::PersonalContactInLE))));
        }

    }
}

