{
//    super(_append);
}

public int active()
{
    int ret;

    ret = super();

    if (HMAmendmentRequest.Status ==  HMAmendmentRequestStatus::New)
    {
        DeleteRequest.enabled(true);
        AccepRequest.enabled(true);
        DenyRequest.enabled(true);
        AppendAmendment.enabled(false);

    }
    else
    {
        DeleteRequest.enabled(false);
        AccepRequest.enabled(false);
        DenyRequest.enabled(false);

        if (HMAmendmentRequest.hasRequestLines())
        {
            AppendAmendment.enabled(false);
        }
        else
        {
            AppendAmendment.enabled(true);

        }
    }

    return ret;
}

public void init()
{
    super();
    this.queryBuildDataSource().addSortField(fieldNum(HMAmendmentRequest,RequestDate),SortOrder::Descending);
    this.queryBuildDataSource().addSortField(fieldNum(HMAmendmentRequest,RequestTime),SortOrder::Descending);
}

