public boolean modified()
{
    boolean ret;
    HMSalutation hmSalutation;

    ret = super();

    if (ret)
    {
        hmSalutation = HMSalutation::findBySalutation(this.selection());
        if(hmSalutation && conLen(hmSalutation.Gender) == 1)
        {
            HMPatient_Gender1.selectText(conPeek(hmSalutation.Gender,1));
        }
        else
        {
            HMPatient_Gender1.selection(0);
        }
    }

    return ret;
}

public boolean modified()
{
    container   con;
    HMAgeHelper hmAgeHelper = new HMAgeHelper();
    boolean     ret;
    date        gregorianDate = HMHijriDateHelper::convertHijriToGeorgian(this.text());

    if (HMHijriDateHelper::convertHijriToGeorgian(this.text()) <= DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()))
    {
        ret = super();
        HMPatient.patientBirthDateEdit(true, HMHijriDateHelper::convertHijriToGeorgian(this.text()));
    }
    else
    {
        throw error ('@MAZ13190');
    }

    if (ret)
    {
        con = hmAgeHelper.getYearsMonthsDaysFromDate(gregorianDate);
        HMPatient.HMAgeValue = conPeek(con,1);
        HMPatient_HMAgeValue.value(conPeek(con,1));
        HMPatient.AgeInMonths = conPeek(con,2);
        HMPatient_AgeInMonths.value(conPeek(con,2));
        HMPatient.AgeInDays = conPeek(con,3);
        HMPatient_AgeInDays.value(conPeek(con,3));

        HMPatient.patientBirthDateEdit(true,birthDates);
        PatientAge.text(HMPatient.patientAgeNew());
        patientbirthdate.dateValue(birthDates);
    }

    HMPatient.HMHijriDateOfBirth = HMHijriDateHelper::convertGeorgianToHijri(gregorianDate);

    HMPatient.patientBirthDateEdit(true, gregorianDate);

    return ret;
}

public boolean modified()
{
    HMAgeHelper         hmAgeHelper = new HMAgeHelper();
    boolean ret;
    container con;


    if(this.dateValue() <=DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()))
    {
        ret = super();

        if (ret)
         {
             con = hmAgeHelper.getYearsMonthsDaysFromDate(this.dateValue());
             HMPatient.HMAgeValue = conPeek(con,1);
             HMPatient_HMAgeValue.value(conPeek(con,1));
             HMPatient.AgeInMonths = conPeek(con,2);
             HMPatient_AgeInMonths.value(conPeek(con,2));
             HMPatient.AgeInDays = conPeek(con,3);
             HMPatient_AgeInDays.value(conPeek(con,3));

             HMPatient.patientBirthDateEdit(true,birthDates);
             PatientAge.text(HMPatient.patientAgeNew());
             patientbirthdate.dateValue(birthDates);

            HMPatient.HMHijriDateOfBirth = HMHijriDateHelper::convertGeorgianToHijri(this.dateValue());
         }
    }
    else
    {
        throw error ('@MAZ13190');
    }
    return ret;
}

public boolean validate()
{
    boolean ret;

    ret = super();
    if (ret)
    {
        if (PatientBirthDate.dateValue() > DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()))
        {
            error('@MAZ13190');
            PatientBirthDate.dateValue(dateNull());
            ret = false;
        }
        else
        {
            birthDates = patientbirthdate.dateValue();
        }

    }
    return ret;
}

