public boolean modified()
{
    HMAgeHelper         hmAgeHelper = new HMAgeHelper();
    boolean ret;

    if(this.dateValue() >= DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()))
    {
        ret = super();

        if (ret)
         {
            HMPatient.HMHijriExpiryDate = HMHijriDateHelper::convertGeorgianToHijri(this.dateValue());
         }
    }
    else
    {
        throw error ('Expiration date cannot be set to the past.');
    }
    return ret;
}

