{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (this.dateValue() < DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()))
        {
            error('Expiration date cannot be set to the past.');
            ret = false;
        }
    }
    return ret;
}

