public boolean modified()
{
    HMAgeHelper hmAgeHelper = new HMAgeHelper();
    boolean     ret;
    date        gregorianExpiryDate = HMHijriDateHelper::convertHijriToGeorgian(this.text());

    if (gregorianExpiryDate >= DateTimeUtil::getToday(DateTimeUtil::getUserPreferredTimeZone()))
    {
        ret = super();
    }
    else
    {
        throw error ('Expiration date cannot be set to the past.');
    }

    if (ret)
    {
        HMPatient.HMNationalIdExpiryDate = gregorianExpiryDate;
    }

    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (this.text() != '')
        {
            if (!System.Text.RegularExpressions.Regex::IsMatch(this.text(), #NamePattern))
            {
                warning("@MAZ3120");
                HMPatient.OtherName = HMPatient.orig().OtherName;
            }
        }
    }
    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        element.updatePatientType();
        if (this.checked())
        {
            ExecutiveProtocol.mandatory(true);
        }
        else
        {
            HMPatient.ExecutiveProtocol = 0;
            ExecutiveProtocol.mandatory(false);
        }

    }


    return ret;
}

