public void jumpRef()
{
    Args                             args;
    MenuFunction                     menuFunction;


    HcmWorker                        hcmWorkerLocal;
    DirPerson                        dirPersonLocal;

    hcmWorkerLocal = HcmWorker::find(HMPatient.HMClinicStaff);
    dirPersonLocal=DirPerson::find(hcmWorkerLocal.Person);

    args = new Args();
    args.caller(element);
    if (!dirPersonLocal)
    {
    args.record(dirPersonLocal);
    menuFunction = new MenuFunction(menuitemdisplaystr(HMClinicStaffMembers),MenuItemType::Display);
    menuFunction.run(args);
    }
    else
    {
    args.lookupRecord(dirPersonLocal);
    menuFunction = new MenuFunction(menuitemdisplaystr(HMClinicStaffMembers),MenuItemType::Display);
    menuFunction.run(args);
    }

}

