public boolean modified()
{
    HMClinicStaff           hmClinicStafflocal;
    HMlookupClass           HMlookupClassObj = new HMlookupClass();
    boolean                 ret;

    ret = super();

    if (ret)
    {
        if (this.valueStr() != "")
        {
            hmClinicStafflocal = HMlookupClassObj.checkValidPhysicianName(this.valueStr());

            if (hmClinicStafflocal.RecId)
            {
                element.storeFamilyDoctor(hmClinicStafflocal.RecId);
            }
            else
            {
                warning(strFmt("@SYS316757","@MAZ8131"));
                Physician.text(HMClinicStaff::getPhysicianNameByRecId(HMPatient.HMClinicStaffFamilyDoctor));
            }
        }
        else
        {
            element.storeFamilyDoctor(0);
        }
    }
    return ret;
}

