public boolean modified()
{
    boolean ret;
    str phonePattern = @"^([+]?|[0]{2})[0-9]?[0-9]?[- .]?(\([2-9]\d{2}\)|[2-9]\d{2})[- .]?\d{3}[- .]?\d{4}$";
    ret = super();

    if (ret)
    {
        if (this.text() != "")
        {

            if (!System.Text.RegularExpressions.Regex::IsMatch(this.text(),phonePattern))
            {
                warning("@MAZ3128");
                HMPatient.EmploymentPhone = HMPatient.orig().EmploymentPhone;
            }
        }

    }
    return ret;
}

public boolean modified()
{
    boolean ret;
    str faxPattern = @"^([+]?|[0]{2})[0-9]?[0-9]?[- .]?(\([2-9]\d{2}\)|[2-9]\d{2})[- .]?\d{3}[- .]?\d{4}$";  // same as phone
    ret = super();

    if (ret)
    {
        if (this.text() != "")
        {

            if (!System.Text.RegularExpressions.Regex::IsMatch(this.text(),faxPattern))
            {
                warning("@MAZ3129");
                HMPatient.EmploymentFax = HMPatient.orig().EmploymentFax;
            }
        }

    }
    return ret;
}

public boolean modified()
{
    boolean ret;
    Str EmailPattern =
       @"^(([\w-]+\.)+[\w-]+|([a-zA-Z]{1}|[\w-]{2,}))@"
     + @"((([0-1]?[0-9]{1,2}|25[0-5]|2[0-4][0-9])\.([0-1]?
       [0-9]{1,2}|25[0-5]|2[0-4][0-9])\."
     + @"([0-1]?[0-9]{1,2}|25[0-5]|2[0-4][0-9])\.([0-1]?
       [0-9]{1,2}|25[0-5]|2[0-4][0-9])){1}|"
     + @"([a-zA-Z]+[\w-]+\.)+[a-zA-Z]{2,4})$";

    ret = super();

    if (ret)
    {
        if (this.text() != "")
        {

            if (!System.Text.RegularExpressions.Regex::IsMatch(this.text(),EmailPattern))
            {
                warning("@MAZ3130");
                HMPatient.EmploymentEmail = HMPatient.orig().EmploymentEmail;
            }
        }

    }
    return ret;
}

