public boolean modified()
{
    boolean ret;
    HMPatient           hmPatientLocal;
    DirPartyTable       dirPartyTableLocal;
    CustTable           custTableLocal;
    DirPartyRelationship dirPartyRelationshipLocal;
    ret = super();

    if(ret)
    {
        if (this.valueStr() != "")
        {
            select * from dirPartyRelationshipLocal
                join * from dirPartyTableLocal
                where dirPartyRelationshipLocal.ChildParty == DirPartyTable.RecId
                && dirPartyRelationshipLocal.ParentParty == dirPartyTableLocal.RecId
                && dirPartyTableLocal.Name == this.valueStr();

            if (dirPartyRelationshipLocal.RecId)
            {
                //flag = true;
                element.storeNextToKin(dirPartyRelationshipLocal);
            }

            else
            {
                warning(strFmt("@SYS316757","@MAZ8133"));
                select * from dirPartyRelationshipLocal where dirPartyRelationshipLocal.RecId == HMPatient.DirPartyRelationshipNextToKin;
                NextToKin.text(dirPartyRelationshipLocal.childName());
            }
        }
        else
        {
            element.storeNextToKin(dirPartyRelationshipLocal);

        }


    }

    return ret;
}

