public boolean validate()
{
    boolean ret;

    ret = super();

    if (ret)
    {
        if (this.value() == true && logisticsElectronicAddress.otherPrimaryExists())
        {
            if (Box::yesNo("@SYS304427", DialogButton::No) == DialogButton::No)
            {
                ret = false;
            }
            else
            {
                IsDuplicateContactExist = true;
            }
        }
    }

    return ret;
}

public int selectionChange()
{
    int ret;
    DirPartyLocation            dirPartyLocationLocal;
    DirPartyTable               dirPartyTableLocal;
    LogisticsLocation           logisticsLocationLocal;
    LogisticsElectronicAddress  logisticsElectronicAddressLocal;
    ret = super();

    if (ret)
    {
        select firstOnly * from dirPartyLocationLocal
        join * from dirPartyTableLocal
        join * from logisticsLocationLocal
        join * from logisticsElectronicAddressLocal
        where
        dirPartyLocationLocal.Party == DirPartyTable.RecId
        && dirPartyTableLocal.RecId == dirPartyLocationLocal.Party
        && logisticsLocationLocal.RecId == dirPartyLocationLocal.Location
        && logisticsLocationLocal.RecId == logisticsElectronicAddressLocal.Location
        && logisticsElectronicAddressLocal.HMPreferredTime == LogisticsElectronicAddress_HMPreferredTime.selection();

        if(dirPartyLocationLocal.RecId != 0)
        {
            if (logisticsElectronicAddressLocal.HMPreferredTime == HMPreferredTime::DayTime)
            {
                Box::stop("@MAZ2605", "@SYS152962");
            }
            else if (logisticsElectronicAddressLocal.HMPreferredTime == HMPreferredTime::NightTime)
            {
                 Box::stop("@MAZ2606", "@SYS152962");
            }
            //error("Preferred contact already exist");
            LogisticsElectronicAddress_HMPreferredTime.selection(HMPreferredTime::NULL);
            LogisticsElectronicAddress.HMPreferredTime = HMPreferredTime::NULL;
            ret = false;
        }

    }

    return ret;
}

public boolean modified()
{
    boolean ret;
    DirPartyLocation            dirPartyLocationLocal;
    DirPartyTable               dirPartyTableLocal;
    LogisticsLocation           logisticsLocationLocal;
    LogisticsElectronicAddress  logisticsElectronicAddressLocal;

    ret = super();

    if (ret)
    {
        if(isEmergency.checked())
        {
            select firstOnly * from dirPartyLocationLocal
                join * from dirPartyTableLocal
                join * from logisticsLocationLocal
                join * from logisticsElectronicAddressLocal
                where
                dirPartyLocationLocal.Party == DirPartyTable.RecId
                && dirPartyTableLocal.RecId == dirPartyLocationLocal.Party
                && logisticsLocationLocal.RecId == dirPartyLocationLocal.Location
                && logisticsLocationLocal.RecId == logisticsElectronicAddressLocal.Location
                && logisticsElectronicAddressLocal.IsEmergency == true;

            if (dirPartyLocationLocal.RecId != 0)
            {
                LogisticsElectronicAddress.IsEmergency = NoYes::No;
                isEmergency.checked(NoYes::No);

                Box::stop("@MAZ2599", "@SYS152962");

                ret = false;
            }
        }
    }

    return ret;
}

