[Form]
public class HMPatientChargeEntryOverrideDeductibe extends FormRun
{
}

public void closeOk()
{
    HMCaseTrans hmcaseTrans;
    if(Value.realValue() >= 0)
    {
        ttsBegin;
        hmcaseTrans = hmcaseTrans::find(element.args().record().RecId,true);
        if(DeductibleType.selection() == HMCoPayType::Amount)
        {
            hmcaseTrans.DeductiblePerUnit = Value.realValue();
            hmcaseTrans.Deductible = hmcaseTrans.DeductiblePerUnit * hmcaseTrans.Qty;
            hmcaseTrans.DeductiblePercentage = hmcaseTrans.Deductible/hmcaseTrans.TotalAmount * 100;
        }
        else
        {
            hmcaseTrans.DeductiblePercentage = Value.realValue();
            hmcaseTrans.DeductiblePerUnit = (hmcaseTrans.TotalAmount - hmcaseTrans.DiscountAmount) * (hmcaseTrans.DeductiblePercentage/100);
            hmcaseTrans.Deductible = hmcaseTrans.DeductiblePerUnit * hmcaseTrans.Qty;
        }
        if(hmcaseTrans.DeductiblePercentage > 100)
        {
            throw warning("Deductible value limit exceeded");
        }
        else
        {
            hmcaseTrans.update();
            element.args().caller().refreshDeductible();
            ttsCommit;
            super();
        }
    }
    else
    {
        warning("@MAZ13461");

    }
}

public boolean modified()
{
    boolean ret;

    ret = super();
    if(ret)
    {
        Value.realValue(0);
    }
    return ret;
}

public boolean modified()
{
    boolean ret;

    ret = super();
    if(ret)
    {
        if(deductibleType.selection() == HMCoPayType::Percentage)
        {
            if(this.realValue() > 100)
            {
                warning("Percentage value cannot be greater than 100 percent");
                this.realValue(0);
            }
        }
    }
    return ret;
}
