public RecId getPatientRecId()
{
    if(element.args().menuItemName() == menuitemDisplayStr(HMPatientChargeEntryPaymentOutstandingSO))
    {
        if(!patientRecId)
        {
            if(!salesTable)
            {
                salesTable = element.args().record();
                AccountDetails_SO.visible(false);
                PatientNumberSO.text(SalesTable.InvoiceAccount);
                PatientNameSo.text(SalesTable.invoiceName());
            }
            patientRecId = HMPatient::findByMRN(salesTable.InvoiceAccount).RecId;
            if(!patientRecId)
            {
                warning("@MAZ12517");
                element.close();
            }
        }
    }
    else
    {
        if(!HMResourceSchedulingAppointment)
        {
            HMResourceSchedulingAppointment = element.args().record();
            patientRecId = HMResourceSchedulingAppointment.HMPatient;
        }
        else
        {
            if(!patientRecId)
            {
                patientRecId = HMResourceSchedulingAppointment.HMPatient;
            }
        }
    }
    return patientRecId;

}

public void init()
{

    super();

    if(element.args().menuItemName() == menuitemDisplayStr(HMPatientChargeEntryPaymentOutstanding))
    {
        paymentType = HMChargeEntryPaymentType::OutstandingPayment;
        custTransOpenMap = new Map(Types::Int64,Types::Record);
        custTransPaymentMap = new Map(Types::Int64,Types::Record);
        custTransRefundMap = new Map(Types::Int64,Types::Record);
    }
    else if(element.args().menuItemName() == menuitemDisplayStr(HMPatientChargeEntryPaymentOutstandingSO))
    {
        paymentType = HMChargeEntryPaymentType::InvoicePayment;
        custTransOpenMap = new Map(Types::Int64,Types::Record);
        custTransPaymentMap = new Map(Types::Int64,Types::Record);
        custTransRefundMap = new Map(Types::Int64,Types::Record);
        HMResourceSchedulingAppointmentDS_ds.queryBuildDataSource().enabled(false);
        GroupDetails.visible(false);
        AccountDetails_SO.visible(true);
    }

}

public void refreshDS()
{
    CustTransOpen_ds.executeQuery();
    CustTransOpen_Payment_ds.executeQuery();
    CustTransOpen_Refund_ds.executeQuery();
    HMResourceSchedulingAppointmentDS_ds.executeQuery();
}

private void transactionCompleted()
{
    info("@MAZ11990");
    element.refreshDS();
}

public boolean validateSettlement()
{
    MapEnumerator   mapEnumerator;
    real            invoiceAmount,paymentAmount, refundAmount;
    CustTransOpen   custTransOpenValidate;
    if(custTransPaymentMap.empty())
    {
        warning("No payment marked for settlement");
        return false;
    }

    if(custTransOpenMap.empty() && custTransRefundMap.empty())
    {
        warning("Select either invoice or refund transaction to settle payment");
        return false;
    }

    mapEnumerator = custTransOpenMap.getEnumerator();
    while (mapEnumerator.moveNext())
    {
        custTransOpenValidate =  mapEnumerator.currentValue();
        invoiceAmount += custTransOpenValidate.AmountCur;
    }

    mapEnumerator = custTransPaymentMap.getEnumerator();
    while (mapEnumerator.moveNext())
    {
        custTransOpenValidate =  mapEnumerator.currentValue();
        paymentAmount += abs(custTransOpenValidate.AmountCur);
    }

    mapEnumerator = custTransRefundMap.getEnumerator();
    while (mapEnumerator.moveNext())
    {
        custTransOpenValidate =  mapEnumerator.currentValue();
        refundAmount += custTransOpenValidate.AmountCur;
    }
    if(invoiceAmount == 0)
    {
        if(refundAmount > 0 && paymentAmount < invoiceAmount)
        {
            warning("Cannot settle the transactions, Payment amount is lesser than refund/invoice amount");
            return false;
        }
    }
    return true;
}

public void init()
{
    super();

    if(element.args().menuItemName() == menuitemDisplayStr(HMPatientChargeEntryPaymentOutstanding) || element.args().menuItemName() == menuitemDisplayStr(HMPatientChargeEntryPaymentOutstandingSO))
    {
        CustTransOpen_ds.queryBuildDataSource().addRange(fieldNum(CustTransOpen,AccountNum)).value(SysQuery::value(HMPatient::find(element.getPatientRecId()).getMRN()));
    }
}

/// <summary>
/// Gets or sets whether the open transaction is marked for settlement.
/// </summary>
/// <param name="_set">
/// true if marked for settlement is set; otherwise, false.
/// </param>
/// <param name="_custTransOpen">
/// The current instance of the CustTransOpen table.
/// </param>
/// <param name="_isMarkedForSettlement">
/// The new marked for settlement when the <paramref name="_set"/> is true.
/// </param>
/// <returns>
/// Whether the open transaction is marked for settlement.
/// </returns>

// BP deviation documented
public edit boolean editIsMarkedForSettlement(
    boolean _set,
    CustTransOpen _custTransOpen,
    boolean _isMarked)
{
    boolean isMarked;
    if(_set)
    {
        if(_isMarked)
        {
            if(!custTransOpenMap.exists(_custTransOpen.RecId))
            {
                custTransOpenMap.insert(_custTransOpen.RecId,_custTransOpen);
            }
        }
        else
        {
            if(custTransOpenMap.exists(_custTransOpen.RecId))
            {
                custTransOpenMap.remove(_custTransOpen.RecId);
            }
        }
    }

    isMarked = custTransOpenMap.exists(_custTransOpen.RecId);
    return isMarked;
}

public void init()
{
    super();
    this.queryBuildDataSource().addRange(fieldNum(custTransIdRef,TransRefType)).value(SysQuery::value(CusttransRefType::Invoice));
}

public void init()
{
    super();
    if(element.args().menuItemName() == menuitemDisplayStr(HMPatientChargeEntryPaymentOutstanding) || element.args().menuItemName() == menuitemDisplayStr(HMPatientChargeEntryPaymentOutstandingSO))
    {
        CustTransOpen_Payment_ds.queryBuildDataSource().addRange(fieldNum(CustTransOpen,AccountNum)).value(SysQuery::value(HMPatient::find(element.getPatientRecId()).getMRN()));
        CustTransOpen_Payment_ds.queryBuildDataSource().addRange(fieldNum(CustTransOpen,RecId)).value('((CustTransOpen_Payment.AmountCur<=0))');
    }
}

