[Form]
public class HMPatientChargeEntryPaymentQuickPay extends FormRun
{
    HMSalesInvoiceCreateFormHandler HMSalesInvoiceCreateFormHandler;
    RecId                           batchRecId  ;
    HMInsuranceAccount              insuranceAccount;
    HMInsuranceCarrierBatchNumber   batchNumber;
    HMTpaCode                       hmTPACode;
}

public void closeOk()
{
    FormRun formRun;
    Args argsLocal;
    real    remainingPayment;
    formRun = element.args().caller();
    if(formHasMethod(formRun,identifierStr(getRemainingPayment)))
    {
        remainingPayment = element.args().caller().getRemainingPayment();
        if(HMPatientChargeEntryPayment.Amount > remainingPayment)
        {
            if(Box::yesNo("The amount entered exceeds with the remaining payment, Do you want to continue?",DialogButton::No,"Confirmation") == DialogButton::No)
            {
                return;
            }
        }
    }
    if(HMPatientChargeEntryPayment_ds.validateWrite())
    {
        HMPatientChargeEntryPayment_ds.write();
        argsLocal = new Args();
        HMSalesInvoiceCreateFormHandler = new HMSalesInvoiceCreateFormHandler();
        argsLocal.record(HMPatientChargeEntryPayment);
        argsLocal.caller(element);
        HMSalesInvoiceCreateFormHandler.postPatientAdvancePayment(argslocal);
        super();
        if(formHasMethod(formRun,identifierStr(updateBatchAmountDetailFields)))
        {
            element.args().caller().updateBatchAmountDetailFields(HMPatientChargeEntryPayment.BatchNumber);
        }
    }
}

public void run()
{
    formrun formRun;
    HMInsuranceCarrierBatchNumber   BatchId;
    container                       conInsuranceDetails;
    super();
    formRun = element.args().caller();
    if(formHasMethod(formRun,identifierStr(getInsuranceDetails)))
    {
        conInsuranceDetails = element.args().caller().getInsuranceDetails();
        insuranceAccount = conPeek(conInsuranceDetails,1);
        batchNumber = conPeek(conInsuranceDetails,2);
        batchRecId = HMInsuranceCarrierBatchDetails::findRecIdbByBatchNumber(batchNumber);
        if(!BatchId && !insuranceAccount)
        {
            warning("@MAZ13399");
            element.close();
            return;
        }
    }
    if(formHasMethod(formRun,identifierStr(getTPADetails)))
    {
        hmTPACode = element.args().caller().getTPADetails();
    }
    HMPatientChargeEntryPayment_ds.create();
}

public void saveLedgerJournalTrans(container _jourTransCon)
{
    HMInsuranceCarrierBatchPayments batchPayments;
    CustTrans                       custTrans;
    int i;
    RecId custTransRecId;
    ttsBegin;
    for(i=1;i<=conlen(_jourTransCon);i++)
    {
        batchPayments.clear();
        custTransRecId = LedgerJournalTrans::findRecId(conPeek(_jourTransCon,i),false).CustTransId;
        batchPayments.CustTrans = custTransRecId;
        batchPayments.HMInsuranceCarrierBatchDetails = batchRecId;
        batchPayments.insert();
        custTrans = custTrans::find(custTransRecId);
        custTrans.selectForUpdate(true);
        custTrans.HMInsuranceCarrierBatchDetails = batchRecId;
        custTrans.update();
    }
    ttsCommit;
}

public void initValue()
{
    super();
    HMPatientChargeEntryPayment.CustAccount = insuranceAccount;
    HMPatientChargeEntryPayment.BatchNumber = batchNumber;
    HMPatientChargeEntryPayment.HMTpaCode = hmTPACode;
}

public Common lookupReference(FormReferenceControl _formReferenceControl)
{
    SysReferenceTableLookup sysTableLookup = SysReferenceTableLookup::newParameters(tableNum(HMMerchantDetails), _formReferenceControl);
    Query lookupQuery;
    QueryBuildDataSource qbds;
    QueryBuildRange qbr;
    HMMerchantDetails selectedRecord;

    sysTableLookup.addLookupfield(fieldNum(HMMerchantDetails, Code));
    sysTableLookup.addLookupfield(fieldNum(HMMerchantDetails, Description));

    lookupQuery = new Query();

    qbds = lookupQuery.addDataSource(tableNum(HMMerchantDetails));
    qbds = qbds.addDataSource(tableNum(HMOrganizationalUnitMerchantDetails));
    qbds.relations(true);

    //qbr = qbds.addRange(fieldNum(HMOrganizationalUnitMerchantDetails, HMOrganizationalUnit));
    //qbr.value(SysQuery::value(HMResourceSchedulingAppointment.HMOrganizationalUnitServiceLocation));

    sysTableLookup.parmQuery(lookupQuery);

    selectedRecord = sysTableLookup.performFormLookup();

    return selectedRecord;
}
